import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const UsersList = () => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/users', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setUsers(response.data.data.users);
      } catch (err) {
        setError(err.response?.data?.message || 'Erreur lors du chargement des utilisateurs');
      }
    };

    fetchUsers();
  }, []);

  return (
    <div className="container mx-auto px-4">
      <h2 className="text-2xl font-bold text-primary mb-6">Liste des utilisateurs</h2>
      
      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {users.map(user => (
          <div key={user.id} className="bg-white rounded-lg shadow-md p-6 hover:shadow-lg transition-shadow">
            <h3 className="text-lg font-semibold mb-2">{user.firstName} {user.lastName}</h3>
            <p className="text-gray-600 mb-2">{user.email}</p>
            <p className="text-gray-600 mb-4">RÃ´le: {user.role}</p>
            <Link 
              to={`/users/${user.id}`}
              className="text-primary hover:text-primary/80 font-medium"
            >
              Modifier le profil
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UsersList;
