import React, { useState, useEffect, useContext, useRef, useCallback } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';
import { AuthContext } from '../context/AuthContext';
import { PlusIcon, XMarkIcon, MapPinIcon, ClockIcon, UserIcon, InformationCircleIcon, DocumentIcon, TrashIcon } from '@heroicons/react/24/outline';

// Define event types and show status options
const eventTypes = [
  { value: 'show', label: 'Spectacle', color: '#800000' },       // Rouge Bordeaux
  { value: 'calage', label: 'Calage', color: '#B22222' },        // Rouge velours
  { value: 'rental', label: 'Location de salle', color: '#D4AF37' }, // Gold
  { value: 'event', label: 'Événement', color: '#8B0000' },      // Rouge profond
  { value: 'ticketing', label: 'Billetterie', color: '#0A1128' }, // Navy
  { value: 'permanence', label: 'Permanence', color: '#36454F' }, // Charcoal
  { value: 'technical', label: 'Technique', color: '#A0522D' }   // Brown
];

const showStatusOptions = [
  { value: 'provisional', label: 'Provisoire', color: '#FFC107' },
  { value: 'confirmed', label: 'Confirmé', color: '#4CAF50' },
  { value: 'cancelled', label: 'Annulé', color: '#F44336' },
  { value: 'ticketsOpen', label: 'Billetterie ouverte', color: '#0A1128' } // Navy color
];

const Calendar = () => {
  const { user, api } = useContext(AuthContext);
  const calendarRef = useRef(null);
  
  // State for rooms, events, and filters
  const [rooms, setRooms] = useState([]);
  const [events, setEvents] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState('add');
  const [currentEvent, setCurrentEvent] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [roomFilter, setRoomFilter] = useState('');
  const [typeFilter, setTypeFilter] = useState('');
  const [showAllEvents, setShowAllEvents] = useState(user?.role === 'administrateur');
  
  // Add clipboard state for copy-paste functionality
  const [clipboardEvent, setClipboardEvent] = useState(null);
  
  // Add state variables for selection dialog
  const [showSelectionDialog, setShowSelectionDialog] = useState(false);
  const [selectedTimeInfo, setSelectedTimeInfo] = useState(null);
  
  // Add state for new event form
  const [newEvent, setNewEvent] = useState({
    title: '',
    start: '',
    end: '',
    roomId: '',
    type: 'show',
    showStatus: 'provisional',
    description: '',
    color: eventTypes[0].color
  });

  // Format date for display
  const formatDate = (dateStr) => {
    const date = new Date(dateStr);
    return date.toLocaleString('fr-FR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Fetch rooms from API
  const fetchRooms = useCallback(async () => {
    try {
      const response = await api.get('/rooms');
      setRooms(response.data.data.rooms);
    } catch (error) {
      setError(error.response?.data?.message || 'Erreur lors du chargement des salles');
    }
  }, [api]);

  // Fetch events from API
  const fetchEvents = useCallback(async () => {
    try {
      setError('');
      
      // Build query parameters
      const params = new URLSearchParams();
      
      // Only add filters if they have values
      if (roomFilter) {
        params.append('roomId', roomFilter);
      }
      
      if (typeFilter) {
        params.append('type', typeFilter);
      }
      
      // Add creatorId filter if not showing all events
      if (!showAllEvents) {
        params.append('creatorId', user.id);
      }
      
      const response = await api.get(`/events?${params.toString()}`);
      
      // Transform events for FullCalendar
      const formattedEvents = response.data.data.events.map(event => ({
        id: event.id,
        title: event.title,
        start: event.start,
        end: event.end,
        color: event.color,
        borderColor: event.type === 'show' ? event.color : undefined,
        borderWidth: event.type === 'show' ? 2 : undefined,
        extendedProps: {
          type: event.type,
          description: event.description,
          room: event.Room,
          creator: event.Creator,
          showStatus: event.showStatus
        }
      }));
      
      setEvents(formattedEvents);
    } catch (error) {
      setError(error.response?.data?.message || 'Erreur lors du chargement des événements');
    }
  }, [api, roomFilter, typeFilter, showAllEvents, user?.id]);

  // Importer les locales de FullCalendar
  useEffect(() => {
    // Importer dynamiquement les locales de FullCalendar
    import('@fullcalendar/core/locales/fr').then(() => {
      // Les locales sont chargées
    });
  }, []);

  // Handle event click
  const handleEventClick = useCallback((info) => {
    const eventId = info.event.id;
    setCurrentEvent({
      id: info.event.id,
      title: info.event.title,
      start: info.event.start,
      end: info.event.end,
      color: info.event.backgroundColor,
      extendedProps: info.event.extendedProps
    });
    setModalType('view');
    setShowModal(true);
  }, []);

  // Handle confirm show status change
  const handleConfirmShow = useCallback(async (newStatus) => {
    if (!currentEvent) return;
    
    try {
      setError('');
      setSuccess('');
      
      // Find the status color and label
      const statusOption = showStatusOptions.find(option => option.value === newStatus);
      const statusColor = statusOption ? statusOption.color : currentEvent.color;
      const statusLabel = statusOption ? statusOption.label : newStatus;
      
      // Update the event with new status
      await api.patch(`/events/${currentEvent.id}`, {
        showStatus: newStatus,
        color: statusColor
      });
      
      setSuccess(`Statut du spectacle mis à jour: ${statusLabel}`);
      setShowModal(false);
      fetchEvents();
    } catch (error) {
      setError(error.response?.data?.message || 'Erreur lors de la mise à jour du statut');
    }
  }, [currentEvent, api, fetchEvents]);

  // Handle copy event
  const handleCopyEvent = () => {
    if (!currentEvent) return;
    
    setClipboardEvent({
      title: currentEvent.title,
      type: currentEvent.extendedProps.type,
      roomId: currentEvent.extendedProps.room?.id,
      description: currentEvent.extendedProps.description,
      color: currentEvent.color,
      showStatus: currentEvent.extendedProps.showStatus,
      duration: new Date(currentEvent.end) - new Date(currentEvent.start) // Store duration in ms
    });
    
    setSuccess('Événement copié dans le presse-papier');
    setShowModal(false);
  };
  
  // Add paste event function - will be called when clicking on a date
  const handlePasteEvent = async (info) => {
    if (!clipboardEvent) {
      setError('Aucun événement dans le presse-papier');
      return;
    }
    
    // Create start date from the clicked date and time
    const clickedDate = new Date(info.dateStr);
    
    // Calculate the duration of the original event
    const originalDuration = clipboardEvent.duration;
    
    // Calculate end date based on the original event duration
    const endDate = new Date(clickedDate.getTime() + originalDuration);
    
    // Get the event type color
    const eventTypeObj = eventTypes.find(type => type.value === clipboardEvent.type);
    const eventColor = eventTypeObj ? eventTypeObj.color : eventTypes[0].color;
    
    try {
      // Create event data with all necessary fields
      const eventData = {
        title: clipboardEvent.title,
        start: clickedDate.toISOString(),
        end: endDate.toISOString(),
        roomId: clipboardEvent.roomId,
        type: clipboardEvent.type || 'show',
        showStatus: clipboardEvent.showStatus || 'provisional',
        description: clipboardEvent.description || '',
        color: eventColor,
        creatorId: user.id // Set the current user as creator
      };
      
      // Directly create the event without showing the modal
      await api.post('/events', eventData);
      setSuccess('Événement collé avec succès');
      
      // Refresh events
      fetchEvents();
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setSuccess('');
      }, 3000);
    } catch (error) {
      setError(error.response?.data?.message || 'Erreur lors de la création de l\'événement');
    }
  };
  
  // Handle date click
  const handleDateClick = (info) => {
    // If there's an event in clipboard and user holds Ctrl key (or Cmd on Mac)
    if (clipboardEvent && (info.jsEvent.ctrlKey || info.jsEvent.metaKey)) {
      handlePasteEvent(info);
      return;
    }
    
    // Store the selected time info
    setSelectedTimeInfo(info);
    
    // If there's an event in clipboard, show selection dialog
    if (clipboardEvent) {
      setShowSelectionDialog(true);
      return;
    }
    
    // Original behavior - create new event
    handleNewEvent(info);
  };
  
  // Add the missing handleSelect function
  const handleSelect = (selectInfo) => {
    // Set default room if available
    const defaultRoomId = rooms.length > 0 ? rooms[0].id.toString() : '';
    
    // Format the dates for the form
    const startDate = new Date(selectInfo.start);
    const endDate = new Date(selectInfo.end);
    
    setNewEvent({
      title: '',
      start: startDate.toISOString().slice(0, 16),
      end: endDate.toISOString().slice(0, 16),
      roomId: defaultRoomId,
      type: 'show',
      showStatus: 'provisional',
      description: '',
      color: eventTypes.find(type => type.value === 'show')?.color || '#800000'
    });
    
    setModalType('add');
    setShowModal(true);
  };
  
  // Handle new event creation
  const handleNewEvent = (info) => {
    const startDate = new Date(info.dateStr);
    const endDate = new Date(info.dateStr);
    endDate.setHours(startDate.getHours() + 2); // Default 2 hour event
    
    // Set default room if available
    const defaultRoomId = rooms.length > 0 ? rooms[0].id.toString() : '';
    
    setNewEvent({
      title: '',
      start: startDate.toISOString().slice(0, 16),
      end: endDate.toISOString().slice(0, 16),
      roomId: defaultRoomId, // Set default room
      type: 'show',
      showStatus: 'provisional', // Default status for shows
      description: '',
      color: eventTypes[0].color
    });
    
    setModalType('add');
    setShowModal(true);
  };
  
  // Get room name by ID
  const getRoomName = (roomId) => {
    const room = rooms.find(r => r.id === roomId);
    return room ? room.name : 'Salle inconnue';
  };
  
  // Get event type label
  const getEventTypeLabel = (typeValue) => {
    const type = eventTypes.find(t => t.value === typeValue);
    return type ? type.label : typeValue;
  };

  // Define the renderAdminToggle function
  const renderAdminToggle = () => {
    if (user?.role === 'administrateur') {
      return (
        <div className="mb-4">
          <label className="inline-flex items-center cursor-pointer">
            <input 
              type="checkbox" 
              className="sr-only peer"
              checked={showAllEvents}
              onChange={() => setShowAllEvents(!showAllEvents)}
            />
            <div className="relative w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary/30 rounded-full peer dark:bg-gray-700 peer-checked:after:translate-x-full rtl:peer-checked:after:-translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:start-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all dark:border-gray-600 peer-checked:bg-primary"></div>
            <span className="ms-3 text-sm font-medium text-gray-900 dark:text-gray-300">
              {showAllEvents ? "Voir tous les événements" : "Voir uniquement mes événements"}
            </span>
          </label>
        </div>
      );
    }
    return null;
  };

  // Switch to edit mode
  const handleEdit = () => {
    if (!currentEvent) return;
    
    // Format the dates properly for the datetime-local input
    const startDate = new Date(currentEvent.start);
    const endDate = new Date(currentEvent.end);
    
    setNewEvent({
      title: currentEvent.title,
      start: startDate.toISOString().slice(0, 16),
      end: endDate.toISOString().slice(0, 16),
      roomId: currentEvent.extendedProps.room?.id.toString() || '',
      type: currentEvent.extendedProps.type,
      showStatus: currentEvent.extendedProps.showStatus || 'provisional',
      description: currentEvent.extendedProps.description || '',
      color: currentEvent.color
    });
    
    setModalType('edit');
    setShowModal(true);
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewEvent(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Update color based on event type
    if (name === 'type') {
      const eventTypeObj = eventTypes.find(type => type.value === value);
      if (eventTypeObj) {
        setNewEvent(prev => ({
          ...prev,
          color: eventTypeObj.color
        }));
      }
    }
  };

  // Handle form submission
  // Add this function to check if a day has reached the show limit
  const checkDayShowLimit = (date, roomId) => {
    if (!roomId) return false;
    
    const startOfDay = new Date(date);
    startOfDay.setHours(0, 0, 0, 0);
    
    const endOfDay = new Date(date);
    endOfDay.setHours(23, 59, 59, 999);
    
    // Filter events for the specific day, room, and type
    const showsOnDay = events.filter(event => 
      event.extendedProps.type === 'show' &&
      event.extendedProps.room?.id === parseInt(roomId) &&
      new Date(event.start) >= startOfDay &&
      new Date(event.start) <= endOfDay
    );
    
    return showsOnDay.length >= 5;
  };
  
  // Modify the handleSubmit function to show a warning
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    try {
      // Check show limit before submitting
      if (newEvent.type === 'show') {
        const startDate = new Date(newEvent.start);
        const isLimitReached = checkDayShowLimit(startDate, newEvent.roomId);
        
        if (isLimitReached && modalType === 'add') {
          if (!window.confirm('Attention: Il y a déjà 5 spectacles ce jour-là dans cette salle. Voulez-vous continuer?')) {
            return;
          }
        }
      }
      
      const eventData = {
        title: newEvent.title,
        start: new Date(newEvent.start).toISOString(),
        end: new Date(newEvent.end).toISOString(),
        roomId: parseInt(newEvent.roomId),
        type: newEvent.type,
        showStatus: newEvent.type === 'show' ? newEvent.showStatus : null,
        description: newEvent.description,
        color: newEvent.color
      };
      
      if (modalType === 'add') {
        await api.post('/events', eventData);
        setSuccess('Événement créé avec succès');
      } else {
        await api.patch(`/events/${currentEvent.id}`, eventData);
        setSuccess('Événement mis à jour avec succès');
      }
      
      setShowModal(false);
      fetchEvents();
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setSuccess('');
      }, 3000);
    } catch (error) {
      setError(error.response?.data?.message || 'Erreur lors de la création/modification de l\'événement');
    }
  };

  // Handle delete event
  const handleDelete = async () => {
    if (!currentEvent) return;
    
    if (!window.confirm('Êtes-vous sûr de vouloir supprimer cet événement ?')) {
      return;
    }
    
    try {
      await api.delete(`/events/${currentEvent.id}`);
      setSuccess('Événement supprimé avec succès');
      setShowModal(false);
      fetchEvents();
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setSuccess('');
      }, 3000);
    } catch (error) {
      setError(error.response?.data?.message || 'Erreur lors de la suppression de l\'événement');
    }
  };

  // Fix the useEffect dependency warning
  useEffect(() => {
    const loadData = async () => {
      await Promise.all([fetchRooms(), fetchEvents()]);
    };
    loadData();
  }, [fetchRooms, fetchEvents]); // Dependencies are now properly included

  return (
    <div className="bg-white rounded-xl shadow-lg overflow-hidden">
      {/* Calendar Header */}
      <div className="bg-gradient-to-r from-primary to-secondary p-6 text-white">
        <div className="flex flex-col md:flex-row justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold">Calendrier</h1>
            <p className="text-gold opacity-90 mt-1">Gérez les événements et réservations</p>
          </div>
          <button
            onClick={() => {
              // Set default room if available
              const defaultRoomId = rooms.length > 0 ? rooms[0].id.toString() : '';
              
              setModalType('add');
              setNewEvent({
                title: '',
                start: '',
                end: '',
                roomId: defaultRoomId, // Set default room
                type: 'show',
                description: '',
                color: '#4CAF50'
              });
              setShowModal(true);
            }}
            className="mt-4 md:mt-0 flex items-center px-4 py-2 bg-gold text-primary rounded-lg hover:bg-gold/90 transition-all shadow-md"
          >
            <PlusIcon className="h-5 w-5 mr-2" />
            Nouvel événement
          </button>
        </div>
      </div>
      
      {/* Alerts */}
      {error && (
        <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 m-6 rounded shadow-md" role="alert">
          <p className="font-bold">Erreur</p>
          <p>{error}</p>
        </div>
      )}
      
      {success && (
        <div className="bg-green-100 border-l-4 border-green-500 text-green-700 p-4 m-6 rounded shadow-md" role="alert">
          <p className="font-bold">Succès</p>
          <p>{success}</p>
        </div>
      )}
      
      {/* Filters */}
      <div className="p-6 bg-gray-50 border-b">
        {renderAdminToggle()}
        <div className="flex flex-col md:flex-row gap-4">
          <div className="flex-1">
            <label className="block text-sm font-medium text-gray-700 mb-1">Filtrer par salle</label>
            <select
              value={roomFilter}
              onChange={(e) => setRoomFilter(e.target.value)}
              className="form-input bg-white"
            >
              <option value="">Toutes les salles</option>
              {rooms.map(room => (
                <option key={room.id} value={room.id}>{room.name}</option>
              ))}
            </select>
          </div>
          
          <div className="flex-1">
            <label className="block text-sm font-medium text-gray-700 mb-1">Filtrer par type</label>
            <select
              value={typeFilter}
              onChange={(e) => setTypeFilter(e.target.value)}
              className="form-input bg-white"
            >
              <option value="">Tous les types</option>
              {eventTypes.map(type => (
                <option key={type.value} value={type.value}>{type.label}</option>
              ))}
            </select>
          </div>
        </div>
      </div>
      
      {/* Calendar */}
      <div className="p-4">
        <div className="calendar-container bg-white rounded-lg shadow-md border border-gray-200 overflow-hidden">
          <FullCalendar
            ref={calendarRef}
            plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
            initialView="timeGridWeek"
            headerToolbar={{
              left: 'prev,next today',
              center: 'title',
              right: 'dayGridMonth,timeGridWeek,timeGridDay'
            }}
            locale="fr" // Définir la locale française
            firstDay={1}
            allDaySlot={false}
            slotMinTime="08:00:00"
            slotMaxTime="23:00:00"
            height="auto"
            events={events}
            eventClick={handleEventClick}
            dateClick={handleDateClick}
            selectable={true}
            select={handleSelect}
            selectMirror={true}
            unselectAuto={true}
            buttonText={{
              today: "Aujourd'hui",
              month: "Mois",
              week: "Semaine",
              day: "Jour"
            }}
            eventTimeFormat={{
              hour: '2-digit',
              minute: '2-digit',
              hour12: false
            }}
          />
        </div>
      </div>
      
      {/* Event Modal - Improved design */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-xl w-full max-w-2xl max-h-[90vh] overflow-y-auto">
            {/* Modal Header */}
            <div className="flex justify-between items-center p-6 border-b">
              <h3 className="text-xl font-semibold text-gray-900">
                {modalType === 'add' ? 'Nouvel événement' : 
                 modalType === 'edit' ? 'Modifier l\'événement' : 
                 currentEvent?.title}
              </h3>
              <button
                onClick={() => setShowModal(false)}
                className="text-gray-400 hover:text-gray-500"
              >
                <XMarkIcon className="h-6 w-6" />
              </button>
            </div>
            
            {/* View Event */}
            {modalType === 'view' && currentEvent && (
              <div className="p-6">
                {/* Event Type Badge - Prominently displayed at the top */}
                <div className="mb-6 flex justify-center">
                  <span 
                    className="px-4 py-2 rounded-full text-white text-sm font-medium shadow-sm"
                    style={{ backgroundColor: currentEvent.color }}
                  >
                    {getEventTypeLabel(currentEvent.extendedProps.type)}
                  </span>
                </div>
                
                <div className="bg-gray-50 rounded-lg p-5 mb-4 shadow-sm border border-gray-100">
                  <div className="flex items-center mb-4">
                    <ClockIcon className="h-5 w-5 text-primary mr-3" />
                    <div>
                      <p className="text-xs text-gray-500 uppercase font-semibold">Date et heure</p>
                      <p className="text-sm text-gray-700">
                        {formatDate(currentEvent.start)} - {formatDate(currentEvent.end)}
                      </p>
                    </div>
                  </div>
                  
                  <div className="flex items-center mb-4">
                    <MapPinIcon className="h-5 w-5 text-primary mr-3" />
                    <div>
                      <p className="text-xs text-gray-500 uppercase font-semibold">Lieu</p>
                      <p className="text-sm text-gray-700">
                        {currentEvent.extendedProps.room?.name || 'Aucune salle'}
                      </p>
                    </div>
                  </div>
                  
                  <div className="flex items-center">
                    <UserIcon className="h-5 w-5 text-primary mr-3" />
                    <div>
                      <p className="text-xs text-gray-500 uppercase font-semibold">Créateur</p>
                      <p className="text-sm text-gray-700">
                        {currentEvent.extendedProps.creator?.firstName} {currentEvent.extendedProps.creator?.lastName}
                      </p>
                    </div>
                  </div>
                </div>
                
                {currentEvent.extendedProps.type === 'show' && (
                  <div className="mb-4 p-4 bg-white rounded-lg border border-gray-200 shadow-sm">
                    <p className="text-sm font-medium text-gray-700 mb-3">Statut du spectacle:</p>
                    <div className="flex flex-wrap gap-2">
                      {showStatusOptions.map(status => (
                        <button
                          key={status.value}
                          onClick={() => handleConfirmShow(status.value)}
                          className={`px-3 py-1.5 text-sm font-medium rounded-md transition-all ${
                            currentEvent.extendedProps.showStatus === status.value
                              ? 'text-white shadow-sm'
                              : 'bg-opacity-10 text-gray-700 hover:bg-opacity-20'
                          }`}
                          style={{ 
                            backgroundColor: currentEvent.extendedProps.showStatus === status.value 
                              ? status.color 
                              : `${status.color}22` 
                          }}
                        >
                          {status.label}
                        </button>
                      ))}
                    </div>
                  </div>
                )}
                
                {currentEvent.extendedProps.description && (
                  <div className="mb-4 p-4 bg-white rounded-lg border border-gray-200 shadow-sm">
                    <p className="text-xs text-gray-500 uppercase font-semibold mb-2">Description</p>
                    <p className="text-sm text-gray-600 whitespace-pre-line">
                      {currentEvent.extendedProps.description}
                    </p>
                  </div>
                )}
                
                <div className="flex justify-between mt-6">
                  <div className="space-x-2">
                    <button
                      onClick={handleCopyEvent}
                      className="px-4 py-2 bg-primary text-white rounded-md hover:bg-primary/90 transition-colors shadow-sm"
                    >
                      Copier
                    </button>
                    <button
                      onClick={handleEdit}
                      className="px-4 py-2 bg-gold text-primary rounded-md hover:bg-gold/90 transition-colors shadow-sm"
                    >
                      Modifier
                    </button>
                  </div>
                  <button
                    onClick={handleDelete}
                    className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 transition-colors shadow-sm"
                  >
                    Supprimer
                  </button>
                </div>
              </div>
            )}
            
            {/* Add/Edit Event Form - Restored previous styling */}
            {(modalType === 'add' || modalType === 'edit') && (
              <form onSubmit={handleSubmit} className="p-6 space-y-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Titre</label>
                    <input
                      type="text"
                      name="title"
                      value={newEvent.title}
                      onChange={handleInputChange}
                      className="form-input w-full"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Salle</label>
                    <select
                      name="roomId"
                      value={newEvent.roomId}
                      onChange={handleInputChange}
                      className="form-input w-full"
                      required
                    >
                      <option value="">Sélectionner une salle</option>
                      {rooms.map(room => (
                        <option key={room.id} value={room.id}>{room.name}</option>
                      ))}
                    </select>
                  </div>
                </div>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Début</label>
                    <input
                      type="datetime-local"
                      name="start"
                      value={newEvent.start}
                      onChange={handleInputChange}
                      className="form-input w-full"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Fin</label>
                    <input
                      type="datetime-local"
                      name="end"
                      value={newEvent.end}
                      onChange={handleInputChange}
                      className="form-input w-full"
                      required
                    />
                  </div>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Type d'événement</label>
                  <select
                    name="type"
                    value={newEvent.type}
                    onChange={handleInputChange}
                    className="form-input w-full"
                    required
                  >
                    {eventTypes.map(type => (
                      <option key={type.value} value={type.value}>{type.label}</option>
                    ))}
                  </select>
                </div>
                {newEvent.type === 'show' && (
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Statut du spectacle</label>
                    <select
                      name="showStatus"
                      value={newEvent.showStatus}
                      onChange={handleInputChange}
                      className="form-input w-full"
                    >
                      {showStatusOptions.map(status => (
                        <option key={status.value} value={status.value}>{status.label}</option>
                      ))}
                    </select>
                  </div>
                )}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Description</label>
                  <textarea
                    name="description"
                    value={newEvent.description}
                    onChange={handleInputChange}
                    className="form-input w-full"
                    rows="4"
                  ></textarea>
                </div>
                <div className="flex justify-end space-x-3">
                  <button
                    type="button"
                    onClick={() => setShowModal(false)}
                    className="px-4 py-2 bg-gray-200 text-gray-800 rounded-md hover:bg-gray-300 transition-colors"
                  >
                    Annuler
                  </button>
                  <button
                    type="submit"
                    className="px-4 py-2 bg-primary text-white rounded-md hover:bg-primary/90 transition-colors shadow-sm"
                  >
                    {modalType === 'add' ? 'Créer' : 'Mettre à jour'}
                  </button>
                </div>
              </form>
            )}
          </div>
        </div>
      )}
      
      {/* Selection Dialog */}
      {showSelectionDialog && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-xl w-full max-w-md p-6">
            <h3 className="text-xl font-semibold text-gray-900 mb-4">Que souhaitez-vous faire ?</h3>
            
            <p className="text-gray-600 mb-4">
              {selectedTimeInfo.dateStr ? 
                `Date sélectionnée : ${new Date(selectedTimeInfo.dateStr).toLocaleString('fr-FR', {
                  day: '2-digit',
                  month: 'long',
                  year: 'numeric',
                  hour: '2-digit',
                  minute: '2-digit'
                })}` :
                `Période sélectionnée : ${new Date(selectedTimeInfo.start).toLocaleString('fr-FR', {
                  day: '2-digit',
                  month: 'long',
                  year: 'numeric',
                  hour: '2-digit',
                  minute: '2-digit'
                })} - ${new Date(selectedTimeInfo.end).toLocaleString('fr-FR', {
                  day: '2-digit',
                  month: 'long',
                  year: 'numeric',
                  hour: '2-digit',
                  minute: '2-digit'
                })}`
              }
            </p>
            
            <div className="space-y-3">
              {clipboardEvent && (
                <button
                  onClick={() => {
                    setShowSelectionDialog(false);
                    if (selectedTimeInfo.dateStr) {
                      handlePasteEvent(selectedTimeInfo);
                    } else {
                      // Handle paste for time range selection
                      const clickedDate = new Date(selectedTimeInfo.start);
                      const duration = selectedTimeInfo.end - selectedTimeInfo.start;
                      const endDate = new Date(clickedDate.getTime() + duration);
                      
                      const eventTypeObj = eventTypes.find(type => type.value === clipboardEvent.type);
                      const eventColor = eventTypeObj ? eventTypeObj.color : eventTypes[0].color;
                      
                      const eventData = {
                        title: clipboardEvent.title,
                        start: clickedDate.toISOString(),
                        end: endDate.toISOString(),
                        roomId: clipboardEvent.roomId,
                        type: clipboardEvent.type || 'show',
                        showStatus: clipboardEvent.showStatus || 'provisional',
                        description: clipboardEvent.description || '',
                        color: eventColor,
                        creatorId: user.id
                      };
                      
                      api.post('/events', eventData)
                        .then(() => {
                          setSuccess('Événement collé avec succès');
                          fetchEvents();
                          setTimeout(() => setSuccess(''), 3000);
                        })
                        .catch(error => {
                          setError(error.response?.data?.message || 'Erreur lors de la création de l\'événement');
                        });
                    }
                  }}
                  className="w-full px-4 py-3 bg-primary text-white rounded-md hover:bg-primary/90 transition-colors shadow-sm flex items-center"
                >
                  <span className="flex-1 text-left">Coller l'événement</span>
                </button>
              )}
              <button
                onClick={() => {
                  setShowSelectionDialog(false);
                  if (selectedTimeInfo.dateStr) {
                    handleNewEvent(selectedTimeInfo);
                  } else {
                    // Handle new event for time range selection
                    const startDate = new Date(selectedTimeInfo.start);
                    const endDate = new Date(selectedTimeInfo.end);
                    
                    setNewEvent({
                      title: '',
                      start: startDate.toISOString().slice(0, 16),
                      end: endDate.toISOString().slice(0, 16),
                      roomId: rooms.length > 0 ? rooms[0].id.toString() : '',
                      type: 'show',
                      showStatus: 'provisional',
                      description: '',
                      color: eventTypes.find(type => type.value === 'show')?.color || '#800000'
                    });
                    
                    setModalType('add');
                    setShowModal(true);
                  }
                }}
                className="w-full px-4 py-3 bg-gold text-primary rounded-md hover:bg-gold/90 transition-colors shadow-sm flex items-center"
              >
                <span className="flex-1 text-left">Créer un nouvel événement</span>
              </button>
              <button
                onClick={() => setShowSelectionDialog(false)}
                className="w-full px-4 py-3 bg-gray-200 text-gray-800 rounded-md hover:bg-gray-300 transition-colors flex items-center"
              >
                <span className="flex-1 text-left">Annuler</span>
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Calendar;
