import React, { useState, useRef, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';
import frLocale from '@fullcalendar/core/locales/fr';
import { XMarkIcon } from '@heroicons/react/24/outline';
import EventModal from '../components/EventModal';
import SelectionDialog from '../components/SelectionDialog';
import { useAuth } from '../contexts/AuthContext';
import { fetchEvents, createEvent, updateEvent, deleteEvent } from '../services/eventService';
import { fetchRooms } from '../services/roomService';
import { fetchUsers } from '../services/userService';

const Calendar = () => {
  // State management
  const [events, setEvents] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [showEventModal, setShowEventModal] = useState(false);
  const [showSelectionDialog, setShowSelectionDialog] = useState(false);
  const [selectedTimeSlot, setSelectedTimeSlot] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  
  // Filters state
  const [roomFilter, setRoomFilter] = useState('');
  const [typeFilter, setTypeFilter] = useState('');
  const [userFilter, setUserFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  
  // Refs
  const calendarRef = useRef(null);
  const { currentUser } = useAuth();

  // Constants
  const eventTypes = [
    { value: 'show', label: 'Spectacle', color: '#4F46E5' },
    { value: 'rehearsal', label: 'Répétition', color: '#059669' },
    { value: 'maintenance', label: 'Maintenance', color: '#DC2626' },
    { value: 'private', label: 'Événement privé', color: '#9333EA' }
  ];

  const showStatusOptions = [
    { value: 'confirmed', label: 'Confirmé', color: '#059669' },
    { value: 'pending', label: 'En attente', color: '#EAB308' },
    { value: 'cancelled', label: 'Annulé', color: '#DC2626' }
  ];

  // Fetch initial data
  useEffect(() => {
    const loadInitialData = async () => {
      try {
        const [eventsData, roomsData, usersData] = await Promise.all([
          fetchEvents(),
          fetchRooms(),
          fetchUsers()
        ]);
        setEvents(eventsData);
        setRooms(roomsData);
        setUsers(usersData);
      } catch (err) {
        setError('Erreur lors du chargement des données');
      }
    };
    loadInitialData();
  }, []);

  // Filter events based on selected filters
  const filteredEvents = events.filter(event => {
    if (roomFilter && event.roomId !== roomFilter) return false;
    if (typeFilter && event.type !== typeFilter) return false;
    if (userFilter && event.userId !== userFilter) return false;
    if (statusFilter && event.status !== statusFilter) return false;
    return true;
  });

  // Event handlers
  const handleEventClick = (clickInfo) => {
    setSelectedEvent(clickInfo.event);
    setShowEventModal(true);
  };

  const handleDateClick = (info) => {
    setSelectedTimeSlot({
      start: info.date,
      end: new Date(info.date.getTime() + 60 * 60 * 1000) // 1 hour duration
    });
    setShowSelectionDialog(true);
  };

  const handleTimeSelect = (selectInfo) => {
    setSelectedTimeSlot({
      start: selectInfo.start,
      end: selectInfo.end
    });
    setShowSelectionDialog(true);
  };

  const handleEventSubmit = async (eventData) => {
    try {
      if (selectedEvent) {
        await updateEvent(selectedEvent.id, eventData);
        setSuccess('Événement mis à jour avec succès');
      } else {
        await createEvent(eventData);
        setSuccess('Événement créé avec succès');
      }
      // Refresh events
      const updatedEvents = await fetchEvents();
      setEvents(updatedEvents);
      setShowEventModal(false);
      setShowSelectionDialog(false);
    } catch (err) {
      setError('Erreur lors de la sauvegarde de l\'événement');
    }
  };

  const handleEventDelete = async (eventId) => {
    try {
      await deleteEvent(eventId);
      setSuccess('Événement supprimé avec succès');
      const updatedEvents = await fetchEvents();
      setEvents(updatedEvents);
      setShowEventModal(false);
    } catch (err) {
      setError('Erreur lors de la suppression de l\'événement');
    }
  };

  const resetFilters = () => {
    setRoomFilter('');
    setTypeFilter('');
    setUserFilter('');
    setStatusFilter('');
  };

  // Custom event rendering
  const renderEventContent = (eventInfo) => {
    const eventType = eventTypes.find(type => type.value === eventInfo.event.extendedProps.type);
    const status = showStatusOptions.find(status => status.value === eventInfo.event.extendedProps.status);
    
    return (
      <div className="p-1">
        <div className="font-semibold">{eventInfo.event.title}</div>
        <div className="text-xs">
          {eventInfo.timeText}
          {eventType && <span className="ml-1">• {eventType.label}</span>}
          {status && <span className="ml-1">• {status.label}</span>}
        </div>
      </div>
    );
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold text-gray-900 mb-4">Calendrier</h1>

      {/* Filters */}
      <div className="mb-4 bg-white p-4 rounded-lg shadow-sm">
        <div className="flex flex-col md:flex-row md:items-center space-y-3 md:space-y-0 md:space-x-4">
          <div className="flex-1">
            <label htmlFor="roomFilter" className="block text-sm font-medium text-gray-700 mb-1">
              Salle
            </label>
            <select
              id="roomFilter"
              value={roomFilter}
              onChange={(e) => setRoomFilter(e.target.value)}
              className="block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring-primary sm:text-sm"
            >
              <option value="">Toutes les salles</option>
              {rooms.map(room => (
                <option key={room.id} value={room.id}>
                  {room.name}
                </option>
              ))}
            </select>
          </div>

          <div className="flex-1">
            <label htmlFor="typeFilter" className="block text-sm font-medium text-gray-700 mb-1">
              Type d'événement
            </label>
            <select
              id="typeFilter"
              value={typeFilter}
              onChange={(e) => setTypeFilter(e.target.value)}
              className="block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring-primary sm:text-sm"
            >
              <option value="">Tous les types</option>
              {eventTypes.map(type => (
                <option key={type.value} value={type.value}>
                  {type.label}
                </option>
              ))}
            </select>
          </div>

          <div className="flex-1">
            <label htmlFor="userFilter" className="block text-sm font-medium text-gray-700 mb-1">
              Utilisateur assigné
            </label>
            <select
              id="userFilter"
              value={userFilter}
              onChange={(e) => setUserFilter(e.target.value)}
              className="block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring-primary sm:text-sm"
            >
              <option value="">Tous les utilisateurs</option>
              {users.map(user => (
                <option key={user.id} value={user.id}>
                  {user.firstName} {user.lastName}
                </option>
              ))}
            </select>
          </div>

          {typeFilter === 'show' && (
            <div className="flex-1">
              <label htmlFor="statusFilter" className="block text-sm font-medium text-gray-700 mb-1">
                Statut du spectacle
              </label>
              <select
                id="statusFilter"
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
                className="block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring-primary sm:text-sm"
              >
                <option value="">Tous les statuts</option>
                {showStatusOptions.map(option => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </select>
            </div>
          )}

          <div className="flex-none self-end">
            <button
              onClick={resetFilters}
              className="inline-flex items-center px-3 py-2 border border-gray-300 shadow-sm text-sm leading-4 font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary"
            >
              <XMarkIcon className="h-4 w-4 mr-1" />
              Réinitialiser
            </button>
          </div>
        </div>
      </div>

      {/* Legend */}
      <div className="mb-4 bg-white p-4 rounded-lg shadow-sm">
        <h3 className="text-sm font-medium text-gray-700 mb-2">Légende</h3>
        <div className="flex flex-wrap gap-2">
          {eventTypes.map(type => (
            <div key={type.value} className="flex items-center">
              <span 
                className="h-3 w-3 rounded-full mr-1"
                style={{ backgroundColor: type.color }}
              ></span>
              <span className="text-xs text-gray-600">{type.label}</span>
            </div>
          ))}
          
          {showStatusOptions.map(option => (
            <div key={option.value} className="flex items-center">
              <span 
                className="h-3 w-3 rounded-full mr-1"
                style={{ backgroundColor: option.color }}
              ></span>
              <span className="text-xs text-gray-600">{option.label}</span>
            </div>
          ))}
        </div>
      </div>

      {/* Notifications */}
      {error && (
        <div className="mb-4 bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-md">
          {error}
        </div>
      )}

      {success && (
        <div className="mb-4 bg-green-50 border border-green-200 text-green-700 px-4 py-3 rounded-md">
          {success}
        </div>
      )}

      {/* Calendar */}
      <div className="bg-white rounded-lg shadow-sm overflow-hidden">
        <FullCalendar
          ref={calendarRef}
          plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
          initialView="timeGridWeek"
          headerToolbar={{
            left: 'prev,next today',
            center: 'title',
            right: 'dayGridMonth,timeGridWeek,timeGridDay'
          }}
          locale={frLocale}
          events={filteredEvents}
          eventContent={renderEventContent}
          eventClick={handleEventClick}
          dateClick={handleDateClick}
          select={handleTimeSelect}
          selectable={true}
          selectMirror={true}
          dayMaxEvents={true}
          weekends={true}
          allDaySlot={false}
          slotMinTime="08:00:00"
          slotMaxTime="23:00:00"
          height="auto"
          eventTimeFormat={{
            hour: '2-digit',
            minute: '2-digit',
            hour12: false
          }}
          slotLabelFormat={{
            hour: '2-digit',
            minute: '2-digit',
            hour12: false
          }}
          businessHours={{
            daysOfWeek: [1, 2, 3, 4, 5, 6, 0],
            startTime: '09:00',
            endTime: '22:00',
          }}
          nowIndicator={true}
        />
      </div>

      {/* Modals */}
      <EventModal
        show={showEventModal}
        onClose={() => setShowEventModal(false)}
        event={selectedEvent}
        rooms={rooms}
        users={users}
        eventTypes={eventTypes}
        showStatusOptions={showStatusOptions}
        onSubmit={handleEventSubmit}
        onDelete={handleEventDelete}
      />

      <SelectionDialog
        show={showSelectionDialog}
        onClose={() => setShowSelectionDialog(false)}
        timeSlot={selectedTimeSlot}
        rooms={rooms}
        users={users}
        eventTypes={eventTypes}
        showStatusOptions={showStatusOptions}
        onSubmit={handleEventSubmit}
      />
    </div>
  );
};

export default Calendar;
