import React, { useState, useEffect, useContext, useRef, useCallback } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';
import { AuthContext } from '../context/AuthContext';
import { PlusIcon, XMarkIcon, MapPinIcon, ClockIcon, UserIcon, InformationCircleIcon } from '@heroicons/react/24/outline';

const Calendar = () => {
  const { user, api } = useContext(AuthContext);
  const calendarRef = useRef(null);
  
  // State for rooms, events, and filters
  const [rooms, setRooms] = useState([]);
  const [events, setEvents] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState('add');
  const [currentEvent, setCurrentEvent] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [roomFilter, setRoomFilter] = useState('');
  const [typeFilter, setTypeFilter] = useState('');
  const [showAllEvents, setShowAllEvents] = useState(user?.role === 'administrateur');
  
  // Add clipboard state for copy-paste functionality
  const [clipboardEvent, setClipboardEvent] = useState(null);
  
  // Event type options with colors
  const eventTypes = [
    { value: 'show', label: 'Spectacle', color: '#4CAF50' },
    { value: 'permanence', label: 'Permanence', color: '#2196F3' },
    { value: 'ticketing', label: 'Billetterie', color: '#FF9800' },
    { value: 'technical', label: 'Technique', color: '#9C27B0' },
    { value: 'rental', label: 'Location', color: '#F44336' },
    { value: 'event', label: 'Événement', color: '#795548' }
  ];
  
  // Show status options with colors
  const showStatusOptions = [
    { value: 'provisional', label: 'Prévisionnel', color: '#FFA726' }, // Orange
    { value: 'confirmed', label: 'Confirmé', color: '#66BB6A' },       // Green
    { value: 'ticketsOpen', label: 'Billetterie ouverte', color: '#42A5F5' } // Blue
  ];
  
  // State for new event form
  const [newEvent, setNewEvent] = useState({
    title: '',
    start: '',
    end: '',
    roomId: '',
    type: 'show',
    showStatus: 'provisional', // Default to provisional for shows
    description: '',
    color: '#4CAF50'
  });
  
  // Fetch rooms and events on component mount
  useEffect(() => {
    const loadData = async () => {
      await Promise.all([fetchRooms(), fetchEvents()]);
    };
    loadData();
  }, []);

  // Move these functions inside the component but outside useEffect
  const fetchRooms = useCallback(async () => {
    try {
      const response = await api.get('/rooms');
      setRooms(response.data.data.rooms);
    } catch (error) {
      console.error('Error fetching rooms:', error);
      setError('Erreur lors du chargement des salles');
    }
  }, [api]);
  
  const fetchEvents = useCallback(async () => {
    try {
      const response = await api.get('/events');
      
      // Apply filters if they exist
      let filteredEvents = response.data.data.events;
      
      if (roomFilter) {
        filteredEvents = filteredEvents.filter(event => event.roomId === parseInt(roomFilter));
      }
      
      if (typeFilter) {
        filteredEvents = filteredEvents.filter(event => event.type === typeFilter);
      }
      
      // For administrators, filter based on showAllEvents toggle
      if (user?.role === 'administrateur' && !showAllEvents) {
        filteredEvents = filteredEvents.filter(event => 
          event.Users.some(u => u.id === user.id)
        );
      }
      
      // Format events for FullCalendar
      const formattedEvents = filteredEvents.map(event => {
        // Check if the current user is assigned to this event
        const isUserAssigned = event.Users.some(u => u.id === user.id);
        
        // Determine color based on event type and show status
        let eventColor = event.color;
        
        // If it's a show, use the color based on showStatus
        if (event.type === 'show' && event.showStatus) {
          const statusOption = showStatusOptions.find(option => option.value === event.showStatus);
          if (statusOption) {
            eventColor = statusOption.color;
          }
        }
        
        return {
          id: event.id,
          title: event.title,
          start: event.start,
          end: event.end,
          color: eventColor,
          extendedProps: {
            type: event.type,
            showStatus: event.showStatus,
            description: event.description,
            room: event.Room,
            users: event.Users,
            creator: event.Creator, // Add creator information
            isUserAssigned
          },
          // Add a border to events the user is assigned to
          borderColor: isUserAssigned ? '#000' : event.color,
          borderWidth: isUserAssigned ? 2 : 0
        };
      });
      
      setEvents(formattedEvents);
    } catch (error) {
      console.error('Error fetching events:', error);
      setError('Erreur lors du chargement des événements');
    }
  }, [api, roomFilter, typeFilter, user?.id, user?.role, showAllEvents]);

  useEffect(() => {
    fetchEvents();
  }, [fetchEvents, roomFilter, typeFilter, showAllEvents]);
  
  useEffect(() => {
    fetchRooms();
  }, [fetchRooms]);

  // Handle event click
  const handleEventClick = (info) => {
    const eventId = info.event.id;
    const eventData = {
      id: info.event.id,
      title: info.event.title,
      start: info.event.start,
      end: info.event.end,
      color: info.event.backgroundColor,
      extendedProps: info.event.extendedProps
    };
    
    if (eventData) {
      setCurrentEvent(eventData);
      setModalType('view');
      setShowModal(true);
    }
  };
  
  // Handle date click
  // Add copy event function
  const handleCopyEvent = () => {
    if (!currentEvent) return;
    
    // Store the current event in clipboard
    setClipboardEvent({
      title: currentEvent.title,
      type: currentEvent.extendedProps.type,
      roomId: currentEvent.extendedProps.room?.id,
      description: currentEvent.extendedProps.description,
      color: currentEvent.color,
      duration: new Date(currentEvent.end) - new Date(currentEvent.start) // Store duration in ms
    });
    
    setSuccess('Événement copié dans le presse-papier');
    setShowModal(false);
  };
  
  // Add paste event function - will be called when clicking on a date
  const handlePasteEvent = async (dateStr) => {
    if (!clipboardEvent) {
      setError('Aucun événement dans le presse-papier');
      return;
    }
    
    // Create start date from the clicked date
    const startDate = new Date(dateStr);
    
    // Calculate end date based on the original event duration
    const endDate = new Date(startDate.getTime() + clipboardEvent.duration);
    
    // Get the event type color
    const eventTypeObj = eventTypes.find(type => type.value === clipboardEvent.type);
    const eventColor = eventTypeObj ? eventTypeObj.color : '#4CAF50';
    
    try {
      // Create event data
      const eventData = {
        title: clipboardEvent.title,
        start: startDate.toISOString(),
        end: endDate.toISOString(),
        roomId: clipboardEvent.roomId,
        type: clipboardEvent.type || 'show',
        description: clipboardEvent.description || '',
        color: eventColor
      };
      
      // Directly create the event without showing the modal
      await api.post('/events', eventData);
      setSuccess('Événement collé avec succès');
      
      // Refresh events
      fetchEvents();
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setSuccess('');
      }, 3000);
    } catch (error) {
      setError(error.response?.data?.message || 'Erreur lors de la création de l\'événement');
    }
  };
  
  // Handle date click
  const handleDateClick = (info) => {
    // If there's an event in clipboard and user holds Ctrl key (or Cmd on Mac)
    if (clipboardEvent && (info.jsEvent.ctrlKey || info.jsEvent.metaKey)) {
      handlePasteEvent(info.dateStr);
      return;
    }
    
    // Original behavior
    const startDate = new Date(info.dateStr);
    const endDate = new Date(info.dateStr);
    endDate.setHours(startDate.getHours() + 2); // Default 2 hour event
    
    // Set default room if available
    const defaultRoomId = rooms.length > 0 ? rooms[0].id.toString() : '';
    
    setNewEvent({
      title: '',
      start: startDate.toISOString().slice(0, 16),
      end: endDate.toISOString().slice(0, 16),
      roomId: defaultRoomId, // Set default room
      type: 'show',
      description: '',
      color: '#4CAF50'
    });
    
    setModalType('add');
    setShowModal(true);
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    
    if (name === 'type') {
      // Set color based on event type
      const selectedType = eventTypes.find(type => type.value === value);
      setNewEvent({
        ...newEvent,
        [name]: value,
        color: selectedType ? selectedType.color : '#4CAF50'
      });
    } else {
      setNewEvent({
        ...newEvent,
        [name]: value
      });
    }
  };
  
  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    try {
      // Check if we're adding a show and if there are already 5 shows on this day
      if (newEvent.type === 'show') {
        const eventDate = new Date(newEvent.start).toISOString().split('T')[0];
        
        // Count shows on the same day
        const showsOnSameDay = events.filter(event => {
          const eventStart = new Date(event.start).toISOString().split('T')[0];
          return eventStart === eventDate && event.extendedProps.type === 'show';
        });
        
        // If editing, don't count the current event
        const currentCount = modalType === 'edit' 
          ? showsOnSameDay.filter(event => event.id.toString() !== currentEvent.id.toString()).length 
          : showsOnSameDay.length;
          
        if (currentCount >= 5) {
          setError('Maximum 5 spectacles par jour autorisés');
          return;
        }
      }
      
      // Get the event type color
      const eventTypeObj = eventTypes.find(type => type.value === newEvent.type);
      const eventColor = eventTypeObj ? eventTypeObj.color : '#4CAF50';
      
      // Create the event data with room name and creator ID
      const eventData = {
        ...newEvent,
        color: eventColor,
        // Make sure creatorId is included if not already present
        creatorId: user.id
      };
      
      if (modalType === 'add') {
        await api.post('/events', eventData);
        setSuccess('Événement créé avec succès');
      } else if (modalType === 'edit' && currentEvent) {
        await api.patch(`/events/${currentEvent.id}`, eventData);
        setSuccess('Événement mis à jour avec succès');
      }
      
      // Reset form and close modal
      setNewEvent({
        title: '',
        start: '',
        end: '',
        roomId: '',
        type: 'show',
        description: '',
        color: '#4CAF50'
      });
      
      setShowModal(false);
      fetchEvents();
    } catch (error) {
      setError(error.response?.data?.message || 'Une erreur est survenue');
    }
  };
  
  // Handle event deletion
  const handleDelete = async () => {
    if (!currentEvent) return;
    
    try {
      await api.delete(`/events/${currentEvent.id}`);
      setSuccess('Événement supprimé avec succès');
      setShowModal(false);
      fetchEvents();
    } catch (error) {
      setError(error.response?.data?.message || 'Erreur lors de la suppression');
    }
  };
  
  // Switch to edit mode
  const handleEdit = () => {
    if (!currentEvent) return;
    
    // Format the dates properly for the datetime-local input
    const startDate = new Date(currentEvent.start);
    const endDate = new Date(currentEvent.end);
    
    // Access the room ID from extendedProps.room.id
    const roomId = currentEvent.extendedProps?.room?.id;
    
    setNewEvent({
      title: currentEvent.title,
      start: startDate.toISOString().slice(0, 16),
      end: endDate.toISOString().slice(0, 16),
      roomId: roomId ? roomId.toString() : '',
      type: currentEvent.extendedProps?.type || 'show',
      showStatus: currentEvent.extendedProps?.showStatus || 'provisional',
      description: currentEvent.extendedProps?.description || '',
      color: currentEvent.color
    });
    
    setModalType('edit');
  };
  
  // Format date for display
  const formatDate = (dateString) => {
    const options = { 
      weekday: 'long', 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleDateString('fr-FR', options);
  };
  
  // Get room name by ID
  const getRoomName = (roomId) => {
    const room = rooms.find(r => r.id === roomId);
    return room ? room.name : 'Salle inconnue';
  };
  
  // Get event type label
  const getEventTypeLabel = (typeValue) => {
    const type = eventTypes.find(t => t.value === typeValue);
    return type ? type.label : typeValue;
  };

  // Define the renderAdminToggle function
  const renderAdminToggle = () => {
    if (user?.role === 'administrateur') {
      return (
        <div className="mb-4">
          <label className="inline-flex items-center cursor-pointer">
            <input 
              type="checkbox" 
              className="sr-only peer"
              checked={showAllEvents}
              onChange={() => setShowAllEvents(!showAllEvents)}
            />
            <div className="relative w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-blue-300 dark:peer-focus:ring-blue-800 rounded-full peer dark:bg-gray-700 peer-checked:after:translate-x-full rtl:peer-checked:after:-translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:start-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all dark:border-gray-600 peer-checked:bg-blue-600"></div>
            <span className="ms-3 text-sm font-medium text-gray-900 dark:text-gray-300">
              {showAllEvents ? "Voir tous les événements" : "Voir uniquement mes événements"}
            </span>
          </label>
        </div>
      );
    }
    return null;
  };

  // Fix the useEffect dependency warning
  useEffect(() => {
    const loadData = async () => {
      await Promise.all([fetchRooms(), fetchEvents()]);
    };
    loadData();
  }, [fetchRooms, fetchEvents]); // Add the missing dependencies

  return (
    <div className="bg-white rounded-xl shadow-lg overflow-hidden">
      {/* Calendar Header */}
      <div className="bg-gradient-to-r from-primary to-secondary p-6 text-white">
        <div className="flex flex-col md:flex-row justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold">Calendrier</h1>
            <p className="text-gold opacity-90 mt-1">Gérez les événements et réservations</p>
          </div>
          <button
            onClick={() => {
              // Set default room if available
              const defaultRoomId = rooms.length > 0 ? rooms[0].id.toString() : '';
              
              setModalType('add');
              setNewEvent({
                title: '',
                start: '',
                end: '',
                roomId: defaultRoomId, // Set default room
                type: 'show',
                description: '',
                color: '#4CAF50'
              });
              setShowModal(true);
            }}
            className="mt-4 md:mt-0 flex items-center px-4 py-2 bg-gold text-primary rounded-lg hover:bg-gold/90 transition-all shadow-md"
          >
            <PlusIcon className="h-5 w-5 mr-2" />
            Nouvel événement
          </button>
        </div>
      </div>
      
      {/* Alerts */}
      {error && (
        <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 m-6 rounded shadow-md" role="alert">
          <p className="font-bold">Erreur</p>
          <p>{error}</p>
        </div>
      )}
      
      {success && (
        <div className="bg-green-100 border-l-4 border-green-500 text-green-700 p-4 m-6 rounded shadow-md" role="alert">
          <p className="font-bold">Succès</p>
          <p>{success}</p>
        </div>
      )}
      
      {/* Filters */}
      <div className="p-6 bg-gray-50 border-b">
        {renderAdminToggle()}
        <div className="flex flex-col md:flex-row gap-4">
          <div className="flex-1">
            <label className="block text-sm font-medium text-gray-700 mb-1">Filtrer par salle</label>
            <select
              value={roomFilter}
              onChange={(e) => setRoomFilter(e.target.value)}
              className="form-input bg-white"
            >
              <option value="">Toutes les salles</option>
              {rooms.map(room => (
                <option key={room.id} value={room.id}>{room.name}</option>
              ))}
            </select>
          </div>
          
          <div className="flex-1">
            <label className="block text-sm font-medium text-gray-700 mb-1">Filtrer par type</label>
            <select
              value={typeFilter}
              onChange={(e) => setTypeFilter(e.target.value)}
              className="form-input bg-white"
            >
              <option value="">Tous les types</option>
              {eventTypes.map(type => (
                <option key={type.value} value={type.value}>{type.label}</option>
              ))}
            </select>
          </div>
        </div>
      </div>
      
      {/* Calendar */}
      <div className="p-6">
        <div className="calendar-container bg-white rounded-lg shadow-sm border border-gray-200">
          <FullCalendar
            ref={calendarRef}
            plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
            initialView="timeGridWeek"
            headerToolbar={{
              left: 'prev,next today',
              center: 'title',
              right: 'dayGridMonth,timeGridWeek,timeGridDay'
            }}
            events={events.map(event => ({
              id: event.id.toString(),
              title: event.title,
              start: event.start,
              end: event.end,
              backgroundColor: event.color,
              borderColor: event.color,
              textColor: '#ffffff',
              extendedProps: event
            }))}
            eventClick={handleEventClick}
            dateClick={handleDateClick}
            locale="fr"
            buttonText={{
              today: "Aujourd'hui",
              month: 'Mois',
              week: 'Semaine',
              day: 'Jour'
            }}
            allDaySlot={false}
            slotMinTime="08:00:00"
            slotMaxTime="23:00:00"
            height="auto"
            firstDay={1} // Start week on Monday
            eventTimeFormat={{
              hour: '2-digit',
              minute: '2-digit',
              meridiem: false,
              hour12: false
            }}
            dayHeaderFormat={{
              weekday: 'short',
              day: 'numeric',
              omitCommas: true
            }}
            slotLabelFormat={{
              hour: '2-digit',
              minute: '2-digit',
              hour12: false
            }}
          />
        </div>
      </div>
      
      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-xl w-full max-w-2xl max-h-[90vh] overflow-y-auto">
            {/* Modal Header */}
            <div className="bg-gradient-to-r from-primary to-secondary p-4 text-white flex justify-between items-center rounded-t-xl">
              <h2 className="text-xl font-bold">
                {modalType === 'add' ? 'Nouvel événement' : 
                 modalType === 'edit' ? 'Modifier l\'événement' : 
                 'Détails de l\'événement'}
              </h2>
              <button 
                onClick={() => setShowModal(false)}
                className="text-white hover:text-gold"
              >
                <XMarkIcon className="h-6 w-6" />
              </button>
            </div>
            
            {/* Modal Content */}
            <div className="p-6">
              {modalType === 'view' && currentEvent ? (
                <div className="space-y-6">
                  <div className="flex items-start">
                    <div className="w-16 h-16 rounded-full flex items-center justify-center mr-4" style={{ backgroundColor: currentEvent.color }}>
                      <span className="text-white text-2xl font-bold">{currentEvent.title.charAt(0)}</span>
                    </div>
                    <div>
                      <h3 className="text-2xl font-bold text-gray-800">{currentEvent.title}</h3>
                      <span className="inline-block px-3 py-1 rounded-full text-sm font-medium mt-2" style={{ backgroundColor: currentEvent.color + '20', color: currentEvent.color }}>
                        {getEventTypeLabel(currentEvent.extendedProps.type)}
                      </span>
                      
                      {/* Show status badge for shows */}
                      {currentEvent.extendedProps.type === 'show' && (
                        <span className="inline-block px-3 py-1 rounded-full text-sm font-medium mt-2 ml-2" style={{ backgroundColor: currentEvent.color + '20', color: currentEvent.color }}>
                          {showStatusOptions.find(s => s.value === currentEvent.extendedProps.showStatus)?.label || 'Prévisionnel'}
                        </span>
                      )}
                    </div>
                  </div>
                  
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div className="flex items-start">
                      <ClockIcon className="h-5 w-5 text-gray-500 mr-2 mt-0.5" />
                      <div>
                        <p className="text-sm text-gray-500">Début</p>
                        <p className="font-medium">{formatDate(currentEvent.start)}</p>
                      </div>
                    </div>
                    
                    <div className="flex items-start">
                      <ClockIcon className="h-5 w-5 text-gray-500 mr-2 mt-0.5" />
                      <div>
                        <p className="text-sm text-gray-500">Fin</p>
                        <p className="font-medium">{formatDate(currentEvent.end)}</p>
                      </div>
                    </div>
                  </div>
                  
                  <div className="flex items-start">
                    <MapPinIcon className="h-5 w-5 text-gray-500 mr-2 mt-0.5" />
                    <div>
                      <p className="text-sm text-gray-500">Salle</p>
                      <p className="font-medium">
                        {currentEvent.extendedProps.room ? currentEvent.extendedProps.room.name : 'Salle non spécifiée'}
                      </p>
                    </div>
                  </div>
                  
                  <div className="flex items-start">
                    <UserIcon className="h-5 w-5 text-gray-500 mr-2 mt-0.5" />
                    <div>
                      <p className="text-sm text-gray-500">Créé par</p>
                      <p className="font-medium">
                        {currentEvent.extendedProps.creator ? 
                          `${currentEvent.extendedProps.creator.firstName} ${currentEvent.extendedProps.creator.lastName}` : 
                          'Utilisateur inconnu'}
                      </p>
                    </div>
                  </div>
                  
                  {currentEvent.extendedProps.description && (
                    <div className="mt-6">
                      <div className="flex items-start">
                        <InformationCircleIcon className="h-5 w-5 text-gray-500 mr-2 mt-0.5" />
                        <div>
                          <p className="text-sm text-gray-500">Description</p>
                          <p className="mt-1">{currentEvent.extendedProps.description}</p>
                        </div>
                      </div>
                    </div>
                  )}
                  
                  <div className="flex justify-end space-x-3 mt-8 pt-4 border-t">
                    <button
                      onClick={handleCopyEvent}
                      className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
                    >
                      Copier
                    </button>
                    
                    {/* Show status buttons for shows */}
                    {currentEvent.extendedProps.type === 'show' && (
                      <div className="flex-1 flex space-x-2">
                        {showStatusOptions.map(status => (
                          <button
                            key={status.value}
                            onClick={() => handleConfirmShow(status.value)}
                            className={`px-3 py-2 text-white rounded-lg transition-colors`}
                            style={{ 
                              backgroundColor: currentEvent.extendedProps.showStatus === status.value 
                                ? '#4B5563' 
                                : status.color 
                            }}
                            disabled={currentEvent.extendedProps.showStatus === status.value}
                          >
                            {status.label}
                          </button>
                        ))}
                      </div>
                    )}
                    
                    <button
                      onClick={handleEdit}
                      className="px-4 py-2 bg-primary text-white rounded-lg hover:bg-accent transition-colors"
                    >
                      Modifier
                    </button>
                    <button
                      onClick={handleDelete}
                      className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
                    >
                      Supprimer
                    </button>
                  </div>
                </div>
              ) : (
                <form onSubmit={handleSubmit} className="space-y-6">
                  <div>
                    <label htmlFor="title" className="block text-sm font-medium text-gray-700 mb-1">Titre</label>
                    <input
                      type="text"
                      id="title"
                      name="title"
                      value={newEvent.title}
                      onChange={handleInputChange}
                      className="form-input bg-white"
                      required
                    />
                  </div>
                  
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label htmlFor="start" className="block text-sm font-medium text-gray-700 mb-1">Début</label>
                      <input
                        type="datetime-local"
                        id="start"
                        name="start"
                        value={newEvent.start}
                        onChange={handleInputChange}
                        className="form-input bg-white"
                        required
                      />
                    </div>
                    
                    <div>
                      <label htmlFor="end" className="block text-sm font-medium text-gray-700 mb-1">Fin</label>
                      <input
                        type="datetime-local"
                        id="end"
                        name="end"
                        value={newEvent.end}
                        onChange={handleInputChange}
                        className="form-input bg-white"
                        required
                      />
                    </div>
                  </div>
                  
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label htmlFor="roomId" className="block text-sm font-medium text-gray-700 mb-1">Salle</label>
                      <select
                        id="roomId"
                        name="roomId"
                        value={newEvent.roomId}
                        onChange={handleInputChange}
                        className="form-input bg-white"
                        required
                      >
                        <option value="">Sélectionner une salle</option>
                        {rooms.map(room => (
                          <option key={room.id} value={room.id}>{room.name}</option>
                        ))}
                      </select>
                    </div>
                    
                    <div>
                      <label htmlFor="type" className="block text-sm font-medium text-gray-700 mb-1">Type d'événement</label>
                      <select
                        id="type"
                        name="type"
                        value={newEvent.type}
                        onChange={handleInputChange}
                        className="form-input bg-white"
                      >
                        {eventTypes.map(type => (
                          <option key={type.value} value={type.value}>{type.label}</option>
                        ))}
                      </select>
                    </div>
                  </div>
                  
                  {/* Show status field - only visible for shows */}
                  {newEvent.type === 'show' && (
                    <div>
                      <label htmlFor="showStatus" className="block text-sm font-medium text-gray-700 mb-1">Statut du spectacle</label>
                      <select
                        id="showStatus"
                        name="showStatus"
                        value={newEvent.showStatus || 'provisional'}
                        onChange={handleInputChange}
                        className="form-input bg-white"
                      >
                        {showStatusOptions.map(status => (
                          <option key={status.value} value={status.value}>{status.label}</option>
                        ))}
                      </select>
                    </div>
                  )}
                  
                  <div>
                    <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-1">Description</label>
                    <textarea
                      id="description"
                      name="description"
                      value={newEvent.description}
                      onChange={handleInputChange}
                      rows="3"
                      className="form-input bg-white"
                    ></textarea>
                  </div>
                  
                  <div className="flex justify-end space-x-3 pt-4 border-t">
                    <button
                      type="button"
                      onClick={() => setShowModal(false)}
                      className="px-4 py-2 bg-gray-200 text-gray-800 rounded-lg hover:bg-gray-300 transition-colors"
                    >
                      Annuler
                    </button>
                    <button
                      type="submit"
                      className="px-4 py-2 bg-primary text-white rounded-lg hover:bg-accent transition-colors"
                    >
                      {modalType === 'add' ? 'Créer' : 'Mettre à jour'}
                    </button>
                  </div>
                </form>
              )}
            </div>
          </div>
        </div>
      )}

      {/* Clipboard indicator */}
      {clipboardEvent && (
        <div className="fixed bottom-4 right-4 bg-blue-100 text-blue-800 px-4 py-2 rounded-lg shadow-md">
          <p className="text-sm">Événement dans le presse-papier</p>
          <p className="text-xs text-blue-600">Ctrl+Clic sur une date pour coller</p>
        </div>
      )}
    </div>
  );
};

export default Calendar;
