import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';
import { 
  XMarkIcon, 
  PlusIcon, 
  TrashIcon, 
  PencilIcon, 
  ClockIcon, 
  MapPinIcon, 
  UserIcon, 
  ClipboardIcon,
  CheckIcon,
  UserGroupIcon,
  DocumentIcon,
  ArrowRightIcon,
  ArrowLeftIcon
} from '@heroicons/react/24/outline';

const Calendar = () => {
  const { user, api } = useContext(AuthContext);
  const calendarRef = useRef(null);
  
  // États pour les données
  const [events, setEvents] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  
  // États pour les filtres
  const [roomFilter, setRoomFilter] = useState('');
  const [typeFilter, setTypeFilter] = useState('');
  const [showAllEvents, setShowAllEvents] = useState(true);
  
  
  // États pour le modal
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState('add'); // 'add', 'edit', 'view'
  const [currentEvent, setCurrentEvent] = useState(null);
  const [newEvent, setNewEvent] = useState({
    title: '',
    start: '',
    end: '',
    roomId: '',
    type: 'show',
    showStatus: 'provisional',
    description: '',
    coRealizationPercentage: '',
    color: '',
    assignedUsers: [],
    ticketingLocation: '', 
    hasDecor: false,
    decorDetails: ''
  });
  // États pour la sélection d'événement
  const [showSelectionDialog, setShowSelectionDialog] = useState(false);
  const [selectedTimeInfo, setSelectedTimeInfo] = useState(null);
  
  // États pour les messages
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  
  // État pour le presse-papier
  const [clipboardEvent, setClipboardEvent] = useState(null);
  
  // Options pour les types d'événements
  const eventTypes = [
    { value: 'show', label: 'Spectacle', color: '#0000FF', canOverlapWith: ['permanence', 'ticketing', 'régie'] },
    { value: 'permanence', label: 'Permanence', color: '#4B0082', canOverlapWith: ['show', 'ticketing', 'régie', 'rental', 'calage', 'event'] },
    { value: 'ticketing', label: 'Billetterie', color: '#008000', canOverlapWith: ['show', 'permanence', 'régie', 'rental', 'calage', 'event'] },
    { value: 'régie', label: 'Régie', color: '#0000FF', canOverlapWith: ['show', 'permanence', 'ticketing', 'rental', 'calage', 'event'] },
    { value: 'rental', label: 'Location salle', color: '#FF8C00', canOverlapWith: ['permanence', 'ticketing', 'régie'] },
    { value: 'calage', label: 'Calage', color: '#800080', canOverlapWith: ['permanence', 'ticketing', 'régie'] },
    { value: 'event', label: 'Événement', color: '#FF1493', canOverlapWith: ['permanence', 'ticketing', 'régie'] }
  ];
  
  // Options pour les statuts de spectacle
  const showStatusOptions = [
    { value: 'confirmed', label: 'Confirmé', color: '#008000' },
    { value: 'provisional', label: 'Provisoire', color: '#FFA500' },
    { value: 'ticketsOpen', label: 'Billetterie ouverte', color: '#0000FF' },
    { value: 'cancelled', label: 'Annulé', color: '#FF0000' }
  ];
  
  // Fonction pour récupérer les événements
  const fetchEvents = useCallback(async () => {
    try {
      let url = '/events';
      const params = [];
      
      if (roomFilter) params.push(`roomId=${roomFilter}`);
      if (typeFilter) params.push(`type=${typeFilter}`);
      if (!showAllEvents) params.push(`creatorId=${user.id}`);
      
      if (params.length > 0) {
        url += `?${params.join('&')}`;
      }
      
      const response = await api.get(url);
      const fetchedEvents = response.data.data.events.map(event => ({
        id: event.id,
        title: event.title,
        start: event.start,
        end: event.end,
        color: event.color,
        extendedProps: {
          type: event.type,
          description: event.description,
          room: event.Room,
          creator: event.Creator,
          users: event.Users,
          showStatus: event.showStatus,
          coRealizationPercentage: event.coRealizationPercentage,
          ticketingLocation: event.ticketingLocation,
          hasDecor: event.hasDecor,
          decorDetails: event.decorDetails,
        }
      }));
      
      setEvents(fetchedEvents);
    } catch (error) {
      setError(error.response?.data?.message || 'Erreur lors de la récupération des événements');
    }
  }, [api, roomFilter, typeFilter, showAllEvents, user.id]);
  
  // Fonction pour récupérer les salles
  const fetchRooms = useCallback(async () => {
    try {
      const response = await api.get('/rooms');
      setRooms(response.data.data.rooms);
    } catch (error) {
      setError(error.response?.data?.message || 'Erreur lors de la récupération des salles');
    }
  }, [api]);
  
  // Fonction pour récupérer les utilisateurs
  const fetchUsers = useCallback(async () => {
    try {
      const response = await api.get('/users');
      setUsers(response.data.data.users);
    } catch (error) {
      setError(error.response?.data?.message || 'Erreur lors de la récupération des utilisateurs');
    }
  }, [api]);
  
  // Effet pour charger les données initiales
  useEffect(() => {
    fetchRooms();
    fetchUsers();
    fetchEvents();
  }, [fetchRooms, fetchUsers, fetchEvents]);
  
  // Effet pour filtrer les utilisateurs en fonction du type d'événement
  useEffect(() => {
    if (!users.length) return;
    
    let filtered = [...users];
    
    // Filtrer les utilisateurs en fonction du type d'événement
    switch (newEvent.type) {
      case 'show':
        filtered = users.filter(user => ['artiste', 'administrateur'].includes(user.role));
        break;
      case 'ticketing':
        filtered = users.filter(user => ['billeterie', 'employé', 'administrateur'].includes(user.role));
        break;
      case 'régie':
        filtered = users.filter(user => ['regie', 'administrateur'].includes(user.role));
        break;
      case 'permanence':
        filtered = users.filter(user => ['permanence', 'administrateur'].includes(user.role));
        break;
      case 'event':
      case 'calage':
      case 'rental':
        filtered = []; // No users can be assigned to these types
        break;
      default:
        filtered = users;
    }
    
    setFilteredUsers(filtered);
  }, [users, newEvent.type]);
  
  // Helper function to check if a room already has 5 shows on a given day
  const checkShowLimit = async (roomId, date) => {
    try {
      // Format the date to YYYY-MM-DD for the API query
      const formattedDate = date.toISOString().split('T')[0];
      
      // Get all events for this room on this day
      const response = await api.get(`/events?roomId=${roomId}&date=${formattedDate}`);
      const roomEvents = response.data.data.events;
      
      // Count shows on this day
      const showCount = roomEvents.filter(event => {
        const eventDate = new Date(event.start);
        return event.type === 'show' && 
               eventDate.toISOString().split('T')[0] === formattedDate;
      }).length;
      
      return showCount >= 5;
    } catch (error) {
      console.error('Error checking show limit:', error);
      return false; // In case of error, allow the operation to proceed
    }
  };
  
  // Fonction pour formater la date pour l'affichage
  const formatDate = (dateString) => {
    if (!dateString) return '';
    
    const date = new Date(dateString);
    return date.toLocaleString('fr-FR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };
  
  // Fonction pour formater la date pour les inputs
  const formatDateForInput = (dateString) => {
    if (!dateString) return '';
    
    const date = new Date(dateString);
    return new Date(date.getTime() - date.getTimezoneOffset() * 60000)
      .toISOString()
      .slice(0, 16);
  };
  
  // Fonction pour obtenir le libellé d'un type d'événement
  const getEventTypeLabel = (type) => {
    const eventType = eventTypes.find(t => t.value === type);
    return eventType ? eventType.label : type;
  };
  
  // Fonction pour créer un nouvel événement
  const handleNewEvent = (info) => {
    const startDate = info.start || info.date || new Date();
    let endDate = info.end || new Date(startDate.getTime() + 2 * 60 * 60 * 1000);
    
    // Si la sélection est sur une journée entière, ajuster l'heure de fin
    if (info.allDay) {
      endDate = new Date(startDate);
      endDate.setHours(startDate.getHours() + 2);
    }
    
    setNewEvent({
      title: '',
      start: formatDateForInput(startDate),
      end: formatDateForInput(endDate),
      roomId: rooms.length > 0 ? rooms[0].id.toString() : '',
      type: 'show',
      showStatus: 'provisional',
      description: '',
      coRealizationPercentage: '',
      color: eventTypes.find(t => t.value === 'show').color,
      assignedUsers: [],
      ticketingLocation: '',
      hasDecor: false,
      decorDetails: ''
    });
    
    setModalType('add');
    setShowModal(true);
  };
  
  // Fonction pour gérer le clic sur un événement
  const handleEventClick = useCallback((info) => {
    const event = info.event;
    setCurrentEvent({
      id: event.id,
      title: event.title,
      start: event.start,
      end: event.end,
      color: event.backgroundColor,
      extendedProps: event.extendedProps
    });
    setModalType('view');
    setShowModal(true);
  }, []);

  // Fonction pour gérer la sélection d'utilisateurs
  const handleUserSelection = (userId) => {
    setNewEvent(prev => {
      const assignedUsers = [...prev.assignedUsers];
      
      if (assignedUsers.includes(userId)) {
        // Retirer l'utilisateur s'il est déjà sélectionné
        return {
          ...prev,
          assignedUsers: assignedUsers.filter(id => id !== userId)
        };
      } else {
        // Ajouter l'utilisateur s'il n'est pas déjà sélectionné
        return {
          ...prev,
          assignedUsers: [...assignedUsers, userId]
        };
      }
    });
  };
  
  // Fonction pour afficher la section de sélection d'utilisateurs
  const renderUserSelectionSection = () => {
    if (modalType === 'view') return null;

    // Define event types that don't allow user assignment
    const disabledEventTypes = ['event', 'calage', 'rental'];
    
    // If current event type doesn't allow user assignment, show message
    if (disabledEventTypes.includes(newEvent.type)) {
      return (
        <div className="mt-4">
          <p className="text-sm text-gray-500 italic">
            L'assignation d'utilisateurs n'est pas disponible pour ce type d'événement
          </p>
        </div>
      );
    }
    
    return (
      <div className="mt-4">
        <label className="block text-sm font-medium mb-2">
          Utilisateurs assignés
        </label>
        
        {filteredUsers.length > 0 ? (
          <div className="border rounded-md p-2 max-h-60 overflow-y-auto">
            {filteredUsers.map(user => (
              <div 
                key={user.id}
                className="flex items-center mb-2 last:mb-0"
              >
                <input
                  type="checkbox"
                  id={`user-${user.id}`}
                  checked={newEvent.assignedUsers.includes(user.id)}
                  onChange={() => handleUserSelection(user.id)}
                  className="mr-2"
                />
                <label htmlFor={`user-${user.id}`} className="text-sm cursor-pointer">
                  {user.firstName} {user.lastName}
                  <span className="ml-1 text-xs text-gray-500">({user.role})</span>
                </label>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-sm text-gray-500 italic">
            Aucun utilisateur disponible pour ce type d'événement
          </div>
        )}
      </div>
    );
  };

  const handleConfirmShow = useCallback(async (newStatus) => {
    if (!currentEvent) return;
    
    try {
      setError('');
      setSuccess('');
      
      // Trouver la couleur et le libellé du statut
      const statusOption = showStatusOptions.find(option => option.value === newStatus);
      const statusColor = statusOption ? statusOption.color : currentEvent.color;
      
      // Mettre à jour l'événement avec le nouveau statut
      await api.patch(`/events/${currentEvent.id}`, {
        showStatus: newStatus,
        color: statusColor
      });
      
      setSuccess(`Statut du spectacle mis à jour`);
      setShowModal(false);
      fetchEvents();
    } catch (error) {
      setError(error.response?.data?.message || 'Erreur lors de la mise à jour du statut');
    }
  }, [currentEvent, api, fetchEvents]);

  // Gestion du presse-papier
  const handleCopyEvent = () => {
    if (!currentEvent) return;
    
    setClipboardEvent({
      title: currentEvent.title,
      type: currentEvent.extendedProps.type,
      roomId: currentEvent.extendedProps.room?.id,
      description: currentEvent.extendedProps.description,
      color: currentEvent.color,
      showStatus: currentEvent.extendedProps.showStatus,
      duration: new Date(currentEvent.end) - new Date(currentEvent.start),
      assignedUsers: currentEvent.extendedProps.users?.map(user => user.id) || []
    });
    
    setSuccess('Événement copié dans le presse-papier');
    setShowModal(false);
  };
  
  const handlePasteEvent = async (info) => {
    if (!clipboardEvent) {
      setError('Aucun événement dans le presse-papier');
      return;
    }
    
    // Créer la date de début à partir de la date cliquée
    const clickedDate = new Date(info.dateStr || info.start);
    
    // Calculer la date de fin en fonction de la durée de l'événement original
    const endDate = new Date(clickedDate.getTime() + clipboardEvent.duration);
    
    // Trouver le type d'événement et sa couleur
    const eventTypeObj = eventTypes.find(type => type.value === clipboardEvent.type);
    const eventColor = eventTypeObj ? eventTypeObj.color : eventTypes[0].color;
    
    // Check show limit if this is a show
    if (clipboardEvent.type === 'show') {
      const hasReachedLimit = await checkShowLimit(clipboardEvent.roomId, clickedDate);
      if (hasReachedLimit) {
        setError('Impossible d\'ajouter plus de 5 spectacles par jour dans cette salle');
        setTimeout(() => setError(''), 5000);
        return;
      }
    }
    
    // Préparer les données de l'événement
    const eventData = {
      title: clipboardEvent.title,
      start: clickedDate.toISOString(),
      end: endDate.toISOString(),
      roomId: clipboardEvent.roomId,
      type: clipboardEvent.type,
      description: clipboardEvent.description,
      creatorId: user.id,
      color: eventColor,
      showStatus: clipboardEvent.showStatus,
      assignedUsers: clipboardEvent.assignedUsers
    };
    
    try {
      await api.post('/events', eventData);
      setSuccess('Événement collé avec succès');
      fetchEvents();
      setTimeout(() => setSuccess(''), 3000);
    } catch (error) {
      setError(error.response?.data?.message || 'Erreur lors de la création de l\'événement');
      setTimeout(() => setError(''), 5000);
    }
  };

  // Gestion des interactions avec le calendrier
  const handleDateClick = (info) => {
    setSelectedTimeInfo(info);
    setShowSelectionDialog(true);
  };
  
  const handleSelect = (selectInfo) => {
    setSelectedTimeInfo(selectInfo);
    setShowSelectionDialog(true);
  };
  
  // Fonction pour gérer les changements dans le formulaire
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    
    if (type === 'checkbox') {
      setNewEvent(prev => ({
        ...prev,
        [name]: checked
      }));
      return;
    }
    
    // Si le type d'événement change, mettre à jour la couleur
    if (name === 'type') {
      const eventType = eventTypes.find(type => type.value === value);
      setNewEvent(prev => ({
        ...prev,
        [name]: value,
        color: eventType ? eventType.color : prev.color,
        // Réinitialiser le statut du spectacle si le type n'est pas 'show'
        showStatus: value === 'show' ? prev.showStatus : undefined
      }));
    } else {
      setNewEvent(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };
  
  // Fonction pour éditer un événement existant
  const handleEdit = () => {
    if (!currentEvent) return;
    
    setNewEvent({
      id: currentEvent.id,
      title: currentEvent.title,
      start: formatDateForInput(currentEvent.start),
      end: formatDateForInput(currentEvent.end),
      roomId: currentEvent.extendedProps.room?.id || (rooms.length > 0 ? rooms[0].id.toString() : ''),
      type: currentEvent.extendedProps.type || 'show',
      showStatus: currentEvent.extendedProps.showStatus || 'provisional',
      description: currentEvent.extendedProps.description || '',
      coRealizationPercentage: currentEvent.extendedProps.coRealizationPercentage || '',
      color: currentEvent.color,
      assignedUsers: currentEvent.extendedProps.users?.map(user => user.id) || [],
      ticketingLocation: currentEvent.extendedProps.ticketingLocation || '',
      hasDecor: currentEvent.extendedProps.hasDecor || false,
      decorDetails: currentEvent.extendedProps.decorDetails || ''
    });
    
    setModalType('edit');
    setShowModal(true);
  };
  
  const handleDelete = async () => {
    if (!currentEvent) return;
    
    if (!window.confirm('Êtes-vous sûr de vouloir supprimer cet événement ?')) {
      return;
    }
    
    try {
      await api.delete(`/events/${currentEvent.id}`);
      setSuccess('Événement supprimé avec succès');
      setShowModal(false);
      fetchEvents();
      setTimeout(() => setSuccess(''), 3000);
    } catch (error) {
      setError(error.response?.data?.message || 'Erreur lors de la suppression de l\'événement');
    }
  };
  
  // Fonction améliorée pour vérifier si un événement peut chevaucher d'autres événements
  const canEventsOverlap = (movingEventType, stillEventType) => {
    // Trouver la configuration du type d'événement en mouvement
    const movingEventConfig = eventTypes.find(type => type.value === movingEventType);
    
    // Si on ne trouve pas la configuration, ne pas autoriser le chevauchement
    if (!movingEventConfig) return false;
    
    // Vérifier si le type d'événement immobile est dans la liste des types avec lesquels
    // l'événement en mouvement peut se chevaucher
    return movingEventConfig.canOverlapWith.includes(stillEventType);
  };
  
  // Fonction de soumission du formulaire améliorée
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      setError('');
      setSuccess('');
      
      // Check show limit if this is a show and we're adding a new event
      if (newEvent.type === 'show' && modalType === 'add') {
        const hasReachedLimit = await checkShowLimit(newEvent.roomId, new Date(newEvent.start));
        if (hasReachedLimit) {
          setError('Impossible d\'ajouter plus de 5 spectacles par jour dans cette salle');
          return;
        }
      }
      
      // Vérifier les chevauchements d'événements
      if (newEvent.roomId) {
        // Récupérer tous les événements pour la même salle
        const response = await api.get(`/events?roomId=${newEvent.roomId}`);
        const roomEvents = response.data.data.events;
        
        // Convertir les dates du nouvel événement
        const newStart = new Date(newEvent.start);
        const newEnd = new Date(newEvent.end);
        
        // Vérifier les chevauchements
        const overlappingEvents = roomEvents.filter(event => {
          // Ignorer l'événement actuel lors de l'édition
          if (modalType === 'edit' && event.id.toString() === newEvent.id.toString()) {
            return false;
          }
          
          const eventStart = new Date(event.start);
          const eventEnd = new Date(event.end);
          
          // Vérifier si les événements se chevauchent dans le temps
          const isOverlapping = (
            (newStart < eventEnd && newStart >= eventStart) ||
            (newEnd > eventStart && newEnd <= eventEnd) ||
            (newStart <= eventStart && newEnd >= eventEnd)
          );
          
          // Si chevauchement, vérifier si les types d'événements peuvent se chevaucher
          if (isOverlapping) {
            // Vérifier si les types peuvent se chevaucher
            return !canEventsOverlap(newEvent.type, event.type);
          }
          
          return false;
        });
        
        if (overlappingEvents.length > 0) {
          // Trouver les noms des types d'événements qui ne peuvent pas se chevaucher
          const conflictingTypes = [...new Set(overlappingEvents.map(event => 
            getEventTypeLabel(event.type)
          ))];
          
          setError(`Cet événement ne peut pas chevaucher les types suivants : ${conflictingTypes.join(', ')}`);
          return;
        }
      }
      
      // Préparer les données de l'événement
      const eventData = {
        title: newEvent.title,
        start: new Date(newEvent.start).toISOString(),
        end: new Date(newEvent.end).toISOString(),
        roomId: newEvent.roomId,
        type: newEvent.type,
        description: newEvent.description,
        creatorId: user.id,
        assignedUsers: newEvent.assignedUsers,
        hasDecor: newEvent.hasDecor,
        decorDetails: newEvent.decorDetails
      };
      
      // Ajouter le statut du spectacle si c'est un spectacle
      if (newEvent.type === 'show') {
        const statusOption = showStatusOptions.find(option => option.value === newEvent.showStatus);
        eventData.showStatus = newEvent.showStatus;
        eventData.color = statusOption ? statusOption.color : eventTypes.find(t => t.value === 'show').color;
        eventData.coRealizationPercentage = newEvent.coRealizationPercentage;
        
        // Add ticketing location if provided
        if (newEvent.ticketingLocation) {
          eventData.ticketingLocation = newEvent.ticketingLocation;
        }
      } else {
        // Utiliser la couleur du type d'événement
        const typeOption = eventTypes.find(t => t.value === newEvent.type);
        eventData.color = typeOption ? typeOption.color : '#800000';
      }
      
      // Créer ou mettre à jour l'événement
      if (modalType === 'add') {
        await api.post('/events', eventData);
        setSuccess('Événement créé avec succès');
      } else {
        await api.patch(`/events/${newEvent.id}`, eventData);
        setSuccess('Événement mis à jour avec succès');
      }
      
      setShowModal(false);
      fetchEvents();
      setTimeout(() => setSuccess(''), 3000);
    } catch (error) {
      setError(error.response?.data?.message || 'Erreur lors de la création/modification de l\'événement');
      setTimeout(() => setError(''), 5000);
    }
  };

  // Rendu du composant
  return (
    <div>
      {/* En-tête du calendrier */}
      <div className="bg-[#8B0000] text-white p-4 flex justify-between items-center">
        <h2 className="text-xl font-semibold">Calendrier des événements</h2>
        <button
          onClick={() => handleNewEvent({
            start: new Date(),
            end: new Date(new Date().getTime() + 2 * 60 * 60 * 1000)
          })}
          className="bg-[#FFD700] hover:bg-[#FFD700]/90 text-[#8B0000] px-4 py-2 rounded-md flex items-center font-medium"
        >
          <PlusIcon className="h-5 w-5 mr-1" />
          Créer un événement
        </button>
      </div>
      
      {/* Alertes */}
      {error && (
        <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mb-4">
          <p>{error}</p>
        </div>
      )}
      
      {success && (
        <div className="bg-green-100 border-l-4 border-green-500 text-green-700 p-4 mb-4">
          <p>{success}</p>
        </div>
      )}
      
      {/* Filtres */}
      <div className="p-4 flex flex-wrap gap-4">
        <div className="flex-1">
          <label className="block text-sm font-medium text-gray-700 mb-1">Filtrer par salle</label>
          <select
            value={roomFilter}
            onChange={(e) => setRoomFilter(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-md"
          >
            <option value="">Toutes les salles</option>
            {rooms.map(room => (
              <option key={room.id} value={room.id}>{room.name}</option>
            ))}
          </select>
        </div>
        
        <div className="flex-1">
          <label className="block text-sm font-medium text-gray-700 mb-1">Filtrer par type</label>
          <select
            value={typeFilter}
            onChange={(e) => setTypeFilter(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-md"
          >
            <option value="">Tous les types</option>
            {eventTypes.map(type => (
              <option key={type.value} value={type.value}>{type.label}</option>
            ))}
          </select>
        </div>
        
        <div className="flex items-end">
          <label className="flex items-center">
            <input
              type="checkbox"
              checked={showAllEvents}
              onChange={(e) => setShowAllEvents(e.target.checked)}
              className="h-5 w-5 text-[#8B0000]"
            />
            <span className="ml-2 text-sm text-gray-700">
              Afficher tous les événements
            </span>
          </label>
        </div>
      </div>
      
      {/* Calendrier */}
      <div className="p-4">
        <FullCalendar
          ref={calendarRef}
          plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
          initialView="timeGridWeek"
          headerToolbar={{
            left: 'prev,next today',
            center: 'title',
            right: 'dayGridMonth,timeGridWeek,timeGridDay'
          }}
          locale="fr"
          firstDay={1}
          slotMinTime="08:00:00"
          slotMaxTime="23:00:00"
          allDaySlot={false}
          height="auto"
          events={events}
          eventClick={handleEventClick}
          dateClick={handleDateClick}
          selectable={true}
          select={handleSelect}
          eventTimeFormat={{
            hour: '2-digit',
            minute: '2-digit',
            meridiem: false,
            hour12: false
          }}
          buttonText={{
            today: "Aujourd'hui",
            month: 'Mois',
            week: 'Semaine',
            day: 'Jour'
          }}
          dayHeaderFormat={{
            weekday: 'long',
            day: 'numeric',
            month: 'long'
          }}
          eventContent={(eventInfo) => {
            return (
              <div className="p-1 overflow-hidden h-full">
                <div className="text-xs font-bold">{eventInfo.event.title}</div>
                <div className="text-xs">
                  {new Date(eventInfo.event.start).toLocaleTimeString('fr-FR', {
                    hour: '2-digit',
                    minute: '2-digit'
                  })}
                  {' - '}
                  {new Date(eventInfo.event.end).toLocaleTimeString('fr-FR', {
                    hour: '2-digit',
                    minute: '2-digit'
                  })}
                </div>
                <div className="text-xs truncate">
                  {eventInfo.event.extendedProps.room?.name}
                </div>
              </div>
            );
          }}
        />
      </div>
      
      {/* Modal de sélection d'action */}
      {showSelectionDialog && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg shadow-xl w-full max-w-md">
            <div className="p-6">
              <h2 className="text-xl font-bold text-[#8B0000] mb-4">
                Que souhaitez-vous faire ?
              </h2>
              
              <div className="grid grid-cols-1 gap-3">
                <button
                  onClick={() => {
                    setShowSelectionDialog(false);
                    handleNewEvent(selectedTimeInfo);
                  }}
                  className="flex items-center justify-center p-3 bg-[#8B0000] text-white rounded-md hover:bg-[#6B0000] transition-all transform hover:scale-[1.02]"
                >
                  <PlusIcon className="h-5 w-5 mr-2" />
                  Créer un nouvel événement
                </button>
                
                {clipboardEvent && (
                  <button
                    onClick={() => {
                      setShowSelectionDialog(false);
                      handlePasteEvent(selectedTimeInfo);
                    }}
                    className="flex items-center justify-center p-3 bg-[#FFD700] text-[#8B0000] rounded-md hover:bg-[#FFD700]/90 transition-all transform hover:scale-[1.02]"
                  >
                    <ClipboardIcon className="h-5 w-5 mr-2" />
                    Coller l'événement du presse-papier
                  </button>
                )}
                
                <button
                  onClick={() => setShowSelectionDialog(false)}
                  className="p-3 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300 transition-all"
                >
                  Annuler
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
      
      {/* Modal principal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg shadow-xl w-full max-w-2xl max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-xl font-bold text-[#8B0000]">
                  {modalType === 'add' ? 'Ajouter un événement' : 
                   modalType === 'edit' ? 'Modifier l\'événement' : 
                   'Détails de l\'événement'}
                </h2>
                <button
                  onClick={() => {
                    setShowModal(false);
                    setError('');
                    setSuccess('');
                  }}
                  className="text-gray-500 hover:text-gray-700 transition-colors rounded-full hover:bg-gray-100 p-1"
                >
                  <XMarkIcon className="h-6 w-6" />
                </button>
              </div>
              
              {error && (
                <div className="mb-4 p-3 bg-red-100 text-red-700 rounded-md border-l-4 border-red-500 animate-fadeIn">
                  {error}
                </div>
              )}
              
              {success && (
                <div className="mb-4 p-3 bg-green-100 text-green-700 rounded-md border-l-4 border-green-500 animate-fadeIn">
                  {success}
                </div>
              )}
              
              {/* Contenu du modal */}
              {modalType === 'view' ? (
                <div className="animate-fadeIn">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                    <div className="bg-gray-50 p-4 rounded-lg">
                      <h3 className="text-lg font-semibold mb-3 text-[#8B0000]">{currentEvent?.title}</h3>
                      
                      <div className="flex items-center text-gray-600 mb-3 bg-white p-2 rounded-md shadow-sm">
                        <ClockIcon className="h-5 w-5 mr-2 text-[#8B0000]" />
                        <span>
                          {new Date(currentEvent?.start).toLocaleString('fr-FR', {
                            weekday: 'long',
                            day: 'numeric',
                            month: 'long',
                            hour: '2-digit',
                            minute: '2-digit'
                          })}
                          {' - '}
                          {new Date(currentEvent?.end).toLocaleString('fr-FR', {
                            hour: '2-digit',
                            minute: '2-digit'
                          })}
                        </span>
                      </div>
                      
                      <div className="flex items-center text-gray-600 mb-3 bg-white p-2 rounded-md shadow-sm">
                        <MapPinIcon className="h-5 w-5 mr-2 text-[#8B0000]" />
                        <span>{currentEvent?.extendedProps?.room?.name || 'Aucune salle'}</span>
                      </div>
                      
                      <div className="flex items-center text-gray-600 mb-3 bg-white p-2 rounded-md shadow-sm">
                        <div 
                          className="h-4 w-4 rounded-full mr-2" 
                          style={{ backgroundColor: currentEvent?.color }}
                        ></div>
                        <span>{getEventTypeLabel(currentEvent?.extendedProps?.type)}</span>
                        
                        {currentEvent?.extendedProps?.type === 'show' && (
                          <span className="ml-2 px-2 py-0.5 rounded-full text-xs" style={{ 
                            backgroundColor: showStatusOptions.find(s => s.value === currentEvent?.extendedProps?.showStatus)?.color + '33',
                            color: showStatusOptions.find(s => s.value === currentEvent?.extendedProps?.showStatus)?.color
                          }}>
                            {showStatusOptions.find(s => s.value === currentEvent?.extendedProps?.showStatus)?.label || 'Statut inconnu'}
                          </span>
                        )}
                      </div>
                      
                      {currentEvent?.extendedProps?.type === 'show' && currentEvent?.extendedProps?.coRealizationPercentage && (
                        <div className="flex items-center text-gray-600 mb-3 bg-white p-2 rounded-md shadow-sm">
                          <span className="mr-2 font-medium">Co-réalisation:</span>
                          <span className="px-2 py-0.5 bg-[#8B0000]/10 rounded-full text-[#8B0000]">
                            {currentEvent?.extendedProps?.coRealizationPercentage}%
                          </span>
                        </div>
                      )}
                      
                      {currentEvent?.extendedProps?.ticketingLocation && (
                        <div className="flex items-center text-gray-600 mb-3 bg-white p-2 rounded-md shadow-sm">
                          <span className="mr-2 font-medium">Lieu de billetterie:</span>
                          <span>{currentEvent?.extendedProps?.ticketingLocation}</span>
                        </div>
                      )}
                      
                      {currentEvent?.extendedProps?.hasDecor && (
                        <div className="flex items-center text-gray-600 mb-3 bg-white p-2 rounded-md shadow-sm">
                          <span className="mr-2 font-medium">Décor:</span>
                          <span>Oui</span>
                          {currentEvent?.extendedProps?.decorDetails && (
                            <span className="ml-2 italic">({currentEvent?.extendedProps?.decorDetails})</span>
                          )}
                        </div>
                      )}
                    </div>
                    
                    <div className="bg-gray-50 p-4 rounded-lg">
                      {currentEvent?.extendedProps?.description && (
                        <div className="mb-4">
                          <h4 className="text-sm font-semibold text-[#8B0000] mb-2">Description:</h4>
                          <p className="text-gray-600 whitespace-pre-wrap bg-white p-3 rounded-md shadow-sm">{currentEvent?.extendedProps?.description}</p>
                        </div>
                      )}
                      
                      {currentEvent?.extendedProps?.creator && (
                        <div className="flex items-center text-gray-600 mb-3 bg-white p-2 rounded-md shadow-sm">
                          <UserIcon className="h-5 w-5 mr-2 text-[#8B0000]" />
                          <span>
                            Créé par: <span className="font-medium">{currentEvent?.extendedProps?.creator?.firstName} {currentEvent?.extendedProps?.creator?.lastName}</span>
                          </span>
                        </div>
                      )}
                      
                      {currentEvent?.extendedProps?.users && currentEvent?.extendedProps?.users.length > 0 && (
                        <div className="mt-4">
                          <h4 className="text-sm font-semibold text-[#8B0000] mb-2 flex items-center">
                            <UserGroupIcon className="h-5 w-5 mr-2" />
                            Utilisateurs assignés:
                          </h4>
                          <div className="grid grid-cols-1 gap-2 bg-white p-3 rounded-md shadow-sm">
                            {currentEvent?.extendedProps?.users.map(user => (
                              <div key={user.id} className="text-sm text-gray-600 flex items-center p-1 hover:bg-gray-50 rounded-md">
                                <div className="h-2 w-2 rounded-full bg-[#8B0000] mr-2"></div>
                                {user.firstName} {user.lastName} 
                                <span className="ml-1 px-2 py-0.5 bg-gray-100 rounded-full text-xs text-gray-600">
                                  {user.role}
                                </span>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                  
                  <div className="flex flex-wrap gap-2 mt-6">
                    {/* Boutons d'action pour les spectacles */}
                    {currentEvent?.extendedProps?.type === 'show' && (
                      <div className="flex flex-wrap gap-2 w-full mb-4 p-3 bg-gray-50 rounded-lg">
                        <button
                          onClick={() => handleConfirmShow('provisional')}
                          className="px-3 py-1.5 bg-yellow-500 text-white rounded-md text-sm flex items-center hover:bg-yellow-600 transition-all transform hover:scale-[1.02]"
                        >
                          <CheckIcon className="h-4 w-4 mr-1" />
                          Provisoire
                        </button>
                        <button
                          onClick={() => handleConfirmShow('confirmed')}
                          className="px-3 py-1.5 bg-green-600 text-white rounded-md text-sm flex items-center hover:bg-green-700 transition-all transform hover:scale-[1.02]"
                        >
                          <CheckIcon className="h-4 w-4 mr-1" />
                          Confirmé
                        </button>
                        <button
                          onClick={() => handleConfirmShow('ticketsOpen')}
                          className="px-3 py-1.5 bg-blue-600 text-white rounded-md text-sm flex items-center hover:bg-blue-700 transition-all transform hover:scale-[1.02]"
                        >
                          <CheckIcon className="h-4 w-4 mr-1" />
                          Billetterie ouverte
                        </button>
                        <button
                          onClick={() => handleConfirmShow('cancelled')}
                          className="px-3 py-1.5 bg-red-600 text-white rounded-md text-sm flex items-center hover:bg-red-700 transition-all transform hover:scale-[1.02]"
                        >
                          <CheckIcon className="h-4 w-4 mr-1" />
                          Annulé
                        </button>
                      </div>
                    )}
                    
                    {/* Boutons d'action généraux */}
                    <div className="flex flex-wrap gap-2 w-full">
                      <button
                        onClick={handleEdit}
                        className="px-3 py-2 bg-[#8B0000] text-white rounded-md text-sm flex items-center hover:bg-[#6B0000] transition-all transform hover:scale-[1.02]"
                      >
                        <PencilIcon className="h-4 w-4 mr-1" />
                        Modifier
                      </button>
                      <button
                        onClick={handleDelete}
                        className="px-3 py-2 bg-red-600 text-white rounded-md text-sm flex items-center hover:bg-red-700 transition-all transform hover:scale-[1.02]"
                      >
                        <TrashIcon className="h-4 w-4 mr-1" />
                        Supprimer
                      </button>
                      <button
                        onClick={handleCopyEvent}
                        className="px-3 py-2 bg-[#FFD700] text-[#8B0000] rounded-md text-sm flex items-center hover:bg-[#FFD700]/90 transition-all transform hover:scale-[1.02]"
                      >
                        <ClipboardIcon className="h-4 w-4 mr-1" />
                        Copier
                      </button>
                    </div>
                  </div>
                </div>
              ) : (
                <form onSubmit={handleSubmit} className="animate-fadeIn">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4 bg-gray-50 p-4 rounded-lg mb-6">
                    <div className="col-span-1 md:col-span-2">
                      <label className="block text-sm font-medium text-[#8B0000] mb-1">
                        Titre *
                      </label>
                      <input
                        type="text"
                        name="title"
                        value={newEvent.title}
                        onChange={handleInputChange}
                        required
                        className="w-full p-2 border border-gray-300 rounded-md focus:ring-[#8B0000] focus:border-[#8B0000] focus:outline-none"
                        placeholder="Titre de l'événement"
                      />
                    </div>
                    
                    <div>
                      <label className="block text-sm font-medium text-[#8B0000] mb-1">
                        Début *
                      </label>
                      <input
                        type="datetime-local"
                        name="start"
                        value={newEvent.start}
                        onChange={handleInputChange}
                        required
                        className="w-full p-2 border border-gray-300 rounded-md focus:ring-[#8B0000] focus:border-[#8B0000] focus:outline-none"
                      />
                    </div>
                    
                    <div>
                      <label className="block text-sm font-medium text-[#8B0000] mb-1">
                        Fin *
                      </label>
                      <input
                        type="datetime-local"
                        name="end"
                        value={newEvent.end}
                        onChange={handleInputChange}
                        required
                        className="w-full p-2 border border-gray-300 rounded-md focus:ring-[#8B0000] focus:border-[#8B0000] focus:outline-none"
                      />
                    </div>
                    
                    <div>
                      <label className="block text-sm font-medium text-[#8B0000] mb-1">
                        Salle *
                      </label>
                      <select
                        name="roomId"
                        value={newEvent.roomId}
                        onChange={handleInputChange}
                        required
                        className="w-full p-2 border border-gray-300 rounded-md focus:ring-[#8B0000] focus:border-[#8B0000] focus:outline-none"
                      >
                        <option value="">Sélectionner une salle</option>
                        {rooms.map(room => (
                          <option key={room.id} value={room.id}>
                            {room.name} (Capacité: {room.capacity})
                          </option>
                        ))}
                      </select>
                    </div>
                    
                    <div>
                      <label className="block text-sm font-medium text-[#8B0000] mb-1">
                        Type d'événement *
                      </label>
                      <select
                        name="type"
                        value={newEvent.type}
                        onChange={handleInputChange}
                        required
                        className="w-full p-2 border border-gray-300 rounded-md focus:ring-[#8B0000] focus:border-[#8B0000] focus:outline-none"
                      >
                        {eventTypes.map(type => (
                          <option key={type.value} value={type.value}>
                            {type.label}
                          </option>
                        ))}
                      </select>
                    </div>
                    
                    {newEvent.type === 'show' && (
                      <>
                        <div>
                          <label className="block text-sm font-medium text-[#8B0000] mb-1">
                            Statut du spectacle
                          </label>
                          <select
                            name="showStatus"
                            value={newEvent.showStatus}
                            onChange={handleInputChange}
                            className="w-full p-2 border border-gray-300 rounded-md focus:ring-[#8B0000] focus:border-[#8B0000] focus:outline-none"
                          >
                            {showStatusOptions.map(option => (
                              <option key={option.value} value={option.value}>
                                {option.label}
                              </option>
                            ))}
                          </select>
                        </div>
                        
                        <div>
                          <label className="block text-sm font-medium text-[#8B0000] mb-1">
                            Pourcentage de co-réalisation
                          </label>
                          <input
                            type="number"
                            name="coRealizationPercentage"
                            value={newEvent.coRealizationPercentage}
                            onChange={handleInputChange}
                            min="0"
                            max="100"
                            className="w-full p-2 border border-gray-300 rounded-md focus:ring-[#8B0000] focus:border-[#8B0000] focus:outline-none"
                            placeholder="Ex: 50"
                          />
                        </div>
                      </>
                    )}
                    
                    <div className="col-span-1 md:col-span-2">
                      <label className="block text-sm font-medium text-[#8B0000] mb-1">
                        Description
                      </label>
                      <textarea
                        name="description"
                        value={newEvent.description}
                        onChange={handleInputChange}
                        rows="3"
                        className="w-full p-2 border border-gray-300 rounded-md focus:ring-[#8B0000] focus:border-[#8B0000] focus:outline-none"
                        placeholder="Description de l'événement"
                      ></textarea>
                    </div>
                    
                    <div>
                      <label className="block text-sm font-medium text-[#8B0000] mb-1">
                        Lieu de billetterie
                      </label>
                      <input
                        type="text"
                        name="ticketingLocation"
                        value={newEvent.ticketingLocation}
                        onChange={handleInputChange}
                        className="w-full p-2 border border-gray-300 rounded-md focus:ring-[#8B0000] focus:border-[#8B0000] focus:outline-none"
                        placeholder="Ex: Guichet principal"
                      />
                    </div>
                    
                    <div className="flex items-center">
                      <input
                        type="checkbox"
                        id="hasDecor"
                        name="hasDecor"
                        checked={newEvent.hasDecor}
                        onChange={(e) => setNewEvent({...newEvent, hasDecor: e.target.checked})}
                        className="h-4 w-4 text-[#8B0000] rounded border-gray-300 focus:ring-[#8B0000]"
                      />
                      <label htmlFor="hasDecor" className="ml-2 block text-sm text-gray-700">
                        Décor
                      </label>
                    </div>
                    
                    {newEvent.hasDecor && (
                      <div className="col-span-1 md:col-span-2">
                        <label className="block text-sm font-medium text-[#8B0000] mb-1">
                          Détails du décor
                        </label>
                        <textarea
                          name="decorDetails"
                          value={newEvent.decorDetails}
                          onChange={handleInputChange}
                          rows="2"
                          className="w-full p-2 border border-gray-300 rounded-md focus:ring-[#8B0000] focus:border-[#8B0000] focus:outline-none"
                          placeholder="Détails sur le décor"
                        ></textarea>
                      </div>
                    )}
                  </div>
                  
                  {/* Section de sélection des utilisateurs */}
                  <div className="bg-gray-50 p-4 rounded-lg mb-6">
                    {renderUserSelectionSection()}
                  </div>
                  
                  {/* Boutons d'action */}
                  <div className="flex justify-end gap-2 mt-6">
                    <button
                      type="button"
                      onClick={() => {
                        setShowModal(false);
                        setError('');
                      }}
                      className="px-4 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300 transition-all"
                    >
                      Annuler
                    </button>
                    <button
                      type="submit"
                      className="px-4 py-2 bg-[#8B0000] text-white rounded-md hover:bg-[#6B0000] transition-all transform hover:scale-[1.02]"
                    >
                      {modalType === 'add' ? 'Créer' : 'Mettre à jour'}
                    </button>
                  </div>
                </form>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Calendar;
