import React, { createContext, useState, useEffect, useCallback } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isInitialized, setIsInitialized] = useState(false);

  // Create a custom axios instance with default config
  const api = axios.create({
    baseURL: 'http://localhost:5000/api',
    headers: {
      'Content-Type': 'application/json'
    }
  });

  // Add request interceptor to add token to all requests
  api.interceptors.request.use(
    (config) => {
      const storedToken = localStorage.getItem('token');
      if (storedToken) {
        config.headers.Authorization = `Bearer ${storedToken}`;
      }
      return config;
    },
    (error) => Promise.reject(error)
  );

  // Add response interceptor to handle token errors
  api.interceptors.response.use(
    (response) => response,
    (error) => {
      if (error.response?.status === 401 || error.response?.status === 403) {
        // Only clear token if we're initialized (prevents logout loop on startup)
        if (isInitialized) {
          localStorage.removeItem('token');
          setToken(null);
          setUser(null);
        }
      }
      return Promise.reject(error);
    }
  );

  const refreshToken = useCallback(async () => {
    try {
      const storedToken = localStorage.getItem('token');
      if (!storedToken) {
        setLoading(false);
        setIsInitialized(true);
        return;
      }

      const res = await api.get('/users/profile');
      
      if (res.data.status === 'success') {
        setUser(res.data.data.user);
        setToken(storedToken);
      } else {
        // Clear token if response is not successful
        localStorage.removeItem('token');
        setToken(null);
        setUser(null);
      }
    } catch (err) {
      console.error('Token refresh error:', err);
      localStorage.removeItem('token');
      setToken(null);
      setUser(null);
    } finally {
      setLoading(false);
      setIsInitialized(true);
    }
  }, [api]);

  useEffect(() => {
    refreshToken();
  }, [refreshToken]);

  const login = async (email, password) => {
    try {
      const res = await api.post('/users/login', { email, password });
      
      if (res.data.token) {
        localStorage.setItem('token', res.data.token);
        setToken(res.data.token);
        setUser(res.data.data.user);
        setError(null);
      }
      
      return res.data;
    } catch (err) {
      console.error('Login error:', err);
      setError(err.response?.data?.message || 'Une erreur est survenue lors de la connexion');
      throw err;
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    setToken(null);
    setUser(null);
    setError(null);
  };

  const register = async (userData) => {
    try {
      const res = await api.post('/users/register', userData);
      
      if (res.data.token) {
        localStorage.setItem('token', res.data.token);
        setToken(res.data.token);
        setUser(res.data.data.user);
        setError(null);
      }
      
      return res.data;
    } catch (err) {
      console.error('Registration error:', err);
      setError(err.response?.data?.message || 'Une erreur est survenue lors de l\'inscription');
      throw err;
    }
  };

  const updateProfile = async (userData) => {
    try {
      const res = await api.put('/users/profile', userData);
      
      if (res.data.status === 'success') {
        setUser(res.data.data.user);
        setError(null);
      }
      
      return res.data;
    } catch (err) {
      console.error('Profile update error:', err);
      setError(err.response?.data?.message || 'Une erreur est survenue lors de la mise à jour du profil');
      throw err;
    }
  };

  return (
    <AuthContext.Provider value={{
      user,
      token,
      loading,
      error,
      setError,
      login,
      logout,
      register,
      updateProfile,
      refreshToken,
      api
    }}>
      {children}
    </AuthContext.Provider>
  );
};
